  CREATE OR REPLACE PROCEDURE "PRAGG"."PRC_CREATE_REPORT_REQUEST" (
 INPAR_REPORT_ID   IN NUMBER
 ,INPAR_USER_ID     IN VARCHAR2
 ,INPAR_NOTIF_ID    IN NUMBER
 ,OUTPAR_RES        OUT NUMBER--ALAKI
) AS
 /*------------------------------------------------------------------------------*/
  /*
  Programmer Name: Rasool.Jahani
  Editor Name:
  Release Date/Time:1396/05/19-10:00
  Edit Name:
  Version: 1
  Description:darkhast baray ijad yek gozaresh jadid 
  */
  /*------------------------------------------------------------------------------*/

 VAR_REP_REQ_ID        NUMBER;
 VAR_NOTIF_ID          VARCHAR2(50);
 VAR_REPPER_DATE       DATE;
 VAR_INPAR_REPORT_ID   NUMBER;
 V_R                   NUMBER;
 LOC_MEGHDAR           VARCHAR2(4000);
 VAR_TYPE              VARCHAR2(200);
BEGIN

/*yaftane id gozaresh az jadval gozareshat*/
 SELECT
  MAX(ID)
 INTO
  VAR_INPAR_REPORT_ID
 FROM TBL_REPORT
 WHERE H_ID   = (
   SELECT
    H_ID
   FROM TBL_REPORT
   WHERE ID   = INPAR_REPORT_ID
  )
 GROUP BY
  H_ID;

/* darj yek darkhast gozaresh jadid dar jadval TBL_REP_REQ*/

 INSERT INTO TBL_REPREQ (
  REF_REPORT_ID
 ,REF_USER_ID
 ,REQ_DATE
 ,STATUS
 ,REF_LEDGER_PROFILE
 ,REF_PROFILE_TIME
 ,REF_PROFILE_CURRENCY
 ,REF_PROFILE_CUSTOMER
 ,REF_PROFILE_BRANCH
 ,REF_PROFILE_DEPOSIT
 ,REF_PROFILE_LOAN
 ,REF_HID_REPORT
 ,TYPE
 ,CATEGORY
 ) VALUES (
  VAR_INPAR_REPORT_ID
 ,INPAR_USER_ID
 ,SYSDATE
 ,0
 ,(
   SELECT
    REF_LEDGER_PROFILE
   FROM TBL_REPORT_PROFILE
   WHERE REF_REPORT   = VAR_INPAR_REPORT_ID
  )
 ,(
   SELECT
    REF_PROFILE_TIME
   FROM TBL_REPORT_PROFILE
   WHERE REF_REPORT   = VAR_INPAR_REPORT_ID
  )
 ,(
   SELECT
    REF_PROFILE_CURRENCY
   FROM TBL_REPORT_PROFILE
   WHERE REF_REPORT   = VAR_INPAR_REPORT_ID
  )
 ,(
   SELECT
    REF_PROFILE_CUSTOMER
   FROM TBL_REPORT_PROFILE
   WHERE REF_REPORT   = VAR_INPAR_REPORT_ID
  )
 ,(
   SELECT
    REF_PROFILE_BRANCH
   FROM TBL_REPORT_PROFILE
   WHERE REF_REPORT   = VAR_INPAR_REPORT_ID
  )
 ,(
   SELECT
    REF_PROFILE_DEPOSIT
   FROM TBL_REPORT_PROFILE
   WHERE REF_REPORT   = VAR_INPAR_REPORT_ID
  )
 ,(
   SELECT
    REF_PROFILE_LOAN
   FROM TBL_REPORT_PROFILE
   WHERE REF_REPORT   = VAR_INPAR_REPORT_ID
  )
 ,(
   SELECT
    H_ID
   FROM TBL_REPORT
   WHERE ID   = VAR_INPAR_REPORT_ID
  )
 ,(
   SELECT
    TYPE
   FROM TBL_REPORT
   WHERE ID   = VAR_INPAR_REPORT_ID
  )
 ,(
   SELECT
    CATEGORY
   FROM TBL_REPORT
   WHERE ID   = VAR_INPAR_REPORT_ID
  )
 );

 COMMIT;

  /*yaftan id darkhast ijad shode*/
 SELECT
  MAX(ID)
 INTO
  VAR_REP_REQ_ID
 FROM TBL_REPREQ;
  /*--------------------------*/

 SELECT
  TYPE
 INTO
  VAR_TYPE
 FROM TBL_REPORT
 WHERE ID   = VAR_INPAR_REPORT_ID;

  IF( VAR_TYPE = 'duedate' )THEN
    PKG_DUE_DATE.PRC_DUE_DATE_REPORT_VALUE(VAR_INPAR_REPORT_ID);
  ELSIF ( VAR_TYPE = 'IDPS' ) THEN
    PKG_IDPS.PRC_IDPS_REP_VALUE(VAR_INPAR_REPORT_ID);
  ELSIF ( upper(VAR_TYPE) = 'COM2' ) THEN
    PKG_COM.PRC_COM_direct_cost(VAR_INPAR_REPORT_ID);
  ELSIF ( VAR_TYPE = 'LCR' ) THEN
    PKG_LCR.PRC_LCR_REP_VALUE(VAR_INPAR_REPORT_ID);
  ELSIF ( VAR_TYPE = 'duedate_loan' ) THEN
    PKG_DUE_DATE_LOAN.PRC_DUE_DATE_REPORT_VALUE(VAR_INPAR_REPORT_ID);
  ELSIF ( VAR_TYPE = 'NOP' ) THEN
    PKG_NOP.PRC_REPORT_VALUE_NOP(VAR_INPAR_REPORT_ID);
  ELSIF ( UPPER(VAR_TYPE) = 'DDDR' ) THEN
    PKG_DUE_DATE_DEPOSIT_RATE.PRC_DDDR_GET_DETAIL(VAR_INPAR_REPORT_ID);
  ELSIF ( VAR_TYPE = 'GAP_NIIM' ) THEN
    PKG_GAP_NIIM.PRC_GAP_NIIM_REPORT_VALUE(VAR_INPAR_REPORT_ID);
  ELSIF ( UPPER(VAR_TYPE) = 'DDLT' ) THEN
    PKG_DUE_DATE_LOAN_TYPE.prc_DDLT_GET_DETAIL(VAR_INPAR_REPORT_ID);
  ELSIF ( UPPER(VAR_TYPE) = 'COMSTAT' ) THEN
    pkg_comparative_statistic.PRC_com_stat_report_value(VAR_INPAR_REPORT_ID);
  ELSIF ( UPPER(VAR_TYPE) = 'CRINDEX' ) THEN
    pkg_creditRisk_branch_index.PRC_cr_br_index_report_value(VAR_INPAR_REPORT_ID);
  ELSIF ( UPPER(VAR_TYPE) = 'DEPOSIT' ) THEN -- CAT: WES
    DYNAMIC_LQ.pkg_WES.PRC_DEP_REPORT_VALUE(VAR_INPAR_REPORT_ID, VAR_REP_REQ_ID);
  ELSIF ( UPPER(VAR_TYPE) = 'LOAN' ) THEN -- CAT: WES
    DYNAMIC_LQ.pkg_WES.PRC_DEP_REPORT_VALUE(VAR_INPAR_REPORT_ID, VAR_REP_REQ_ID);
  ELSIF ( UPPER(VAR_TYPE) = 'SHOCK' ) THEN
    pkg_shock.PRC_shock_report_value(VAR_INPAR_REPORT_ID); 
  ELSIF ( UPPER(VAR_TYPE) = 'SENSANALYSIS' ) THEN
    pkg_sensivity_analysis_test.PRC_SENS_ANALYSIS_REP_VALUE(VAR_INPAR_REPORT_ID); 
  ELSIF ( UPPER(VAR_TYPE) = 'DURATION2' ) THEN
    PKG_DU_GAP_PROF.PRC_DU_GAP_PROF_REPORT_VALUE(VAR_INPAR_REPORT_ID,VAR_REP_REQ_ID);--PRC_DU_GAP_PROF_REPORT_VALUE
      ELSIF ( UPPER(VAR_TYPE) = 'CAR' ) THEN
    PKG_CAR.PRC_CAR_FINAL_REPORT(VAR_INPAR_REPORT_ID,VAR_REP_REQ_ID);
  ELSIF ( UPPER(VAR_TYPE) = '107' ) THEN
    DYNAMIC_LQ.PRC_REPORT_VALUE(VAR_INPAR_REPORT_ID);
    DYNAMIC_LQ.PRC_AGGREGATION(VAR_REP_REQ_ID);
  ELSIF (UPPER(VAR_TYPE)= 'AES') THEN
    DYNAMIC_LQ.PKG_AES.PRC_REPORT_VALUE(VAR_REP_REQ_ID);
 ELSE

  /*ejra prasijer baray filter kardan dadeh hay gozersh*/
  PRC_REPORT_VALUE(VAR_INPAR_REPORT_ID);

  /*ejra tajmi*/
  PRC_AGGREGATION(VAR_REP_REQ_ID);

  /*PRC_GET_QUERY_REPORT(var_Rep_Req_ID,var_report_name);*/
 END IF;

  /*berozresani vaziat gozaresh be payan yafte dar jadval darkhastha */

 UPDATE TBL_REPREQ
  SET
   STATUS = 1
 WHERE ID   = VAR_REP_REQ_ID;

 COMMIT;

 /* prc_liken(to_date('15-NOV-17'));*/
  /*berozresani elanat*/
 PRC_NOTIFICATION(
  'update'
 ,INPAR_NOTIF_ID
 ,''
 ,''
 ,''
 ,'finished'
 ,0
 ,''
 ,0
 ,VAR_REP_REQ_ID
 ,''
 ,'0'
 ,VAR_NOTIF_ID
 );

  /*khoroji alaki, jeddi nagir*/

 OUTPAR_RES   := VAR_NOTIF_ID;
EXCEPTION
 WHEN OTHERS THEN
  LOC_MEGHDAR   := SQLERRM;
  PRC_NOTIFICATION(
   'update'
  ,INPAR_NOTIF_ID
  ,''
  ,''
  ,''
  ,'error'
  ,0
  ,''
  ,0
  ,VAR_REP_REQ_ID
  ,''
  ,'1'
  ,VAR_NOTIF_ID
  );

  COMMIT;
  DELETE FROM TBL_REPREQ WHERE ID   = VAR_REP_REQ_ID;

  COMMIT;
  LOC_MEGHDAR   := SQLERRM;
  UPDATE TBL_NOTIFICATIONS
   SET
    ERROR = LOC_MEGHDAR
  WHERE ID   = INPAR_NOTIF_ID;

  COMMIT;
/*     INSERT INTO TABLE1 (P) VALUES (LOC_MEGHDAR);*/
/*       LOC_MEGHDAR := DBMS_UTILITY.FORMAT_ERROR_BACKTRACE;*/
/*       INSERT INTO TABLE1 (P) VALUES (LOC_MEGHDAR);*/
END PRC_CREATE_REPORT_REQUEST;

