  var_partition VARCHAR2(100):= to_char(inpar_date,'j');
  var_partition_name VARCHAR2(100):= 'P'||to_char(inpar_date,'j');
  var_execute_query varchar(2000);
  var_exist varchar2(100);





begin

     select count(*) into var_exist from ALL_TAB_PARTITIONS where  table_name= 'TBL_LEDGER_ARCHIVE' and substr(PARTITION_NAME,2) in (var_partition);

     if(var_exist<1)
     then

     EXECUTE IMMEDIATE ' ALTER TABLE TBL_LEDGER_ARCHIVE ADD PARTITION P'||var_partition||' VALUES ('||var_partition||')';
     else 



     EXECUTE IMMEDIATE  'ALTER TABLE TBL_LEDGER_ARCHIVE TRUNCATE PARTITION P'||var_partition||'';


end if;




  /******ebteda hame bargha ra dar TBL_LEDGER_ARCHIVE mirizim va baed az an be tartib sath be sath bala miaim va sum childha ro hesab mikonim ******/



insert into TBL_LEDGER_ARCHIVE(LEDGER_CODE,PARENT_CODE,DEPTH,BALANCE,REF_CUR_ID,name,eff_date,CUR_BALANCE,NUMBER_DATE)
select a.GL_CODE, substr(a.GL_CODE,1,7)||'00', 5, sum (a.final_bal), b.id, max(a.gl_name),inpar_date,0,var_partition from dadekavan_day.balance_mv a, satrap_day.bi_table_arz@satrap b where 
trunc(a.effdate) <= trunc(inpar_date) and a.curr_cod = b.nam and  a.curr_cod ='IRR'  group by GL_CODE,b.id;
commit;

