commit;


select '
insert into TBL_LEDGER_ARCHIVE(LEDGER_CODE,PARENT_CODE,DEPTH,BALANCE,REF_CUR_ID,eff_date,CUR_BALANCE,NUMBER_DATE)
(select PARENT_CODE, 0, 1, sum(BALANCE), REF_CUR_ID ,'''||inpar_date||''',sum(CUR_BALANCE),
'||var_partition||'   from TBL_LEDGER_ARCHIVE PARTITION ('||var_partition_name||') where depth = 2 and EFF_DATE = '''||inpar_date||''' group by PARENT_CODE,REF_CUR_ID);'
into var_execute_query from dual;


