  (SELECT LEDGER_CODE
  FROM pragg.tbl_ledger
    CONNECT BY PRIOR LEDGER_CODE = PARENT_CODE
    START WITH LEDGER_CODE       = i.ledger_code
  ); 
   end if;
 end loop; 
   commit;
END update_ledger_node_id_type;

