 IF
  ( VAR_REF_LEDGER_PROFILE <> REF_LEDGER_PROFILE_NEW OR VAR_REF_PROFILE_TIME <> REF_PROFILE_TIME_NEW OR VAR_REF_PROFILE_CURRENCY <> REF_PROFILE_CURRENCY_NEW
