  CREATE OR REPLACE PROCEDURE "PRAGG"."TEST_GAP_NIIM" AS
 VAR_DATE   DATE;
 VAR_MAX    DATE;
BEGIN
 SELECT
  MAX(TRUNC(DUE_DATE) )
 INTO
  VAR_MAX
 FROM TBL_VALUE_temp;

 VAR_DATE   := SYSDATE;

 LOOP
  INSERT INTO TBL_GAP_NIIM_VALUE ( TYPE,GAP_RATE,EFF_DATE ) ( SELECT
   ID
  ,RATE
  ,DT
  FROM (
    SELECT
     REF_MODALITY_TYPE as id
    ,ROUND(
      SUM(BALANCE) / 30
     ,2
     ) as rate
    FROM TBL_VALUE
    WHERE DUE_DATE BETWEEN TRUNC(VAR_DATE) AND TRUNC(VAR_DATE + 30)
     AND
      REF_MODALITY_TYPE IN (
       11,21
      )
    GROUP BY
     REF_MODALITY_TYPE
   )
  ,    (
    SELECT
     TRUNC(VAR_DATE - 1 + ROWNUM) DT
    FROM DUAL
    CONNECT BY
     ROWNUM < 31
   )
  );

  COMMIT;
  VAR_DATE   := VAR_DATE + 30;
  EXIT WHEN trunc(VAR_DATE) > trunc(VAR_MAX);
 END LOOP;

END TEST_GAP_NIIM;

  CREATE OR REPLACE PROCEDURE "PRAGG"."SATTAR_UPDATE" (INPAR_REF_REQ_ID in number) as  --1246

