 /*=============================================================*/
 /*=============================================================*/
 PROCEDURE PRC_CUR_SENSIVITY_REPORT (
  VAR_REF_ID_REPORT   IN NUMBER
 ,VAR_QUERY_CUR       OUT VARCHAR2
 ) AS

  ID_BRANCH    NUMBER;
  VAR_DATE     DATE;
  ID_LEDGER    NUMBER;
  VAR_QUERY    VARCHAR2(30000);
  SUM_REPORT   VARCHAR2(30000);
 BEGIN

 delete from TBL_CUR_SENSIVITY_REPORT where REF_REPORT = VAR_REF_ID_REPORT;
 commit;

  SELECT
   REF_BRN_PROFILE
  INTO
   ID_BRANCH
  FROM TBL_REPORT
  WHERE ID   = VAR_REF_ID_REPORT;

  SELECT
   CREATE_DATE
  INTO
   VAR_DATE
  FROM TBL_REPORT
  WHERE ID   = VAR_REF_ID_REPORT;

  FOR I IN (
   SELECT
    TRD.REF_ID
   ,TRD.NAME
   ,TRD.PROFILE_ID
   ,ARZ."id"
   FROM TBL_REP_DETAIL_PROFIL_CUR_SECS TRD
   ,    (
     SELECT DISTINCT
      TLA.REF_CUR_ID "id"
     FROM TBL_LEDGER_ARCHIVE TLA
     ,    TBL_CURRENCY TC
     WHERE TLA.REF_CUR_ID   = TC.CUR_ID
      AND
       TLA.REF_CUR_ID <> 4
    ) ARZ
   WHERE TRD.REF_ID   = VAR_REF_ID_REPORT
  ) LOOP
   SELECT
    NVL(
     PKG_LEDGER_REPORT_MAP.FNC_GI_CALC(
      I.PROFILE_ID
     ,VAR_REF_ID_REPORT
     ,I."id"
     ,VAR_DATE
     )
    ,0
    )
   INTO
    SUM_REPORT
   FROM DUAL;

   INSERT INTO TBL_CUR_SENSIVITY_REPORT (
    NAME
   ,REF_CURRENCY
   ,MANDE
   ,PROFILE_ID
   ,REF_REPORT
   ) VALUES (
    I.NAME
   ,I."id"
   ,SUM_REPORT
   ,I.PROFILE_ID
   ,I.REF_ID
   );

  END LOOP;

  COMMIT;
  INSERT INTO TBL_CUR_SENSIVITY_REPORT ( NAME,REF_CURRENCY,MANDE ) SELECT
   B.NAME
  ,A.ID
  ,0
  FROM (
    SELECT DISTINCT
     TLA.REF_CUR_ID AS ID
    FROM TBL_LEDGER_ARCHIVE TLA
    ,    TBL_CURRENCY TC
    WHERE TLA.REF_CUR_ID   = TC.CUR_ID
     AND
      TLA.REF_CUR_ID NOT IN (
       SELECT
        TBL_CUR_SENSIVITY_REPORT.REF_CURRENCY
       FROM TBL_CUR_SENSIVITY_REPORT
      )
     AND
      TLA.REF_CUR_ID <> 4
   ) A
  ,    (
    SELECT DISTINCT
     NAME
    FROM TBL_CUR_SENSIVITY_REPORT
   ) B;

  COMMIT;
  INSERT INTO TBL_CUR_SENSIVITY_REPORT (
   NAME
  ,REF_CURRENCY
  ,MANDE
  ,PROFILE_ID
  ) ( SELECT
   NAME
  ,1
  ,0
  ,PROFILE_ID
  FROM TBL_REP_DETAIL_PROFIL_CUR_SECS
  WHERE REF_ID   = 460
   AND
    NAME NOT IN (
     SELECT DISTINCT
      NAME
     FROM TBL_CUR_SENSIVITY_REPORT
    )
  );

  COMMIT;
  UPDATE TBL_REPORT
   SET
    BALANCE_SENSIVITY = (
     SELECT
      SUM(SUM_RIAL)
     FROM (
       SELECT
        B."id"
       ,A.ARZ * B."rate" AS SUM_RIAL
       FROM (
         SELECT
          A.REF_CURRENCY
         ,A.A - B.B ARZ
         FROM (
           SELECT
            REF_CURRENCY
           ,
            SUM(MANDE) A
           FROM TBL_CUR_SENSIVITY_REPORT
           WHERE NAME IN (
             'b1','a1'
            )
           GROUP BY
            REF_CURRENCY
          ) A
         ,    (
           SELECT
            REF_CURRENCY
           ,
            SUM(MANDE) B
           FROM TBL_CUR_SENSIVITY_REPORT
           WHERE NAME IN (
             'b2','a2'
            )
           GROUP BY
            REF_CURRENCY
          ) B
         WHERE A.REF_CURRENCY   = B.REF_CURRENCY
        ) A
       ,    (
         SELECT
          "id"
         ,NVL(CHANGE_RATE,0) "rate"
         FROM (
           SELECT DISTINCT
            TLA.REF_CUR_ID "id"
           ,
            TC.CUR_NAME "name"
           FROM TBL_LEDGER_ARCHIVE TLA
           ,
                TBL_CURRENCY TC
           WHERE TLA.REF_CUR_ID   = TC.CUR_ID
          ) A
          LEFT JOIN (
           SELECT
            *
           FROM TBL_CURRENCY_REL TCR
           WHERE TCR.REL_DATE                = (
             SELECT
              MAX(REL_DATE)
             FROM TBL_CURRENCY_REL
             WHERE TBL_CURRENCY_REL.REL_DATE   = (
               SELECT
                MAX(REL_DATE)
               FROM TBL_CURRENCY_REL
              )
            )
          ) B ON A."id"   = B.SRC_CUR_ID
         WHERE "id" <> 4
        ) B
       WHERE A.REF_CURRENCY   = B."id"
      )
    )
  WHERE ID   = VAR_REF_ID_REPORT;

  COMMIT;
  UPDATE TBL_CUR_SENSIVITY_REPORT T
   SET
    T.PROFILE_ID = (
     SELECT DISTINCT
      C.PROFILE_ID
     FROM TBL_REP_DETAIL_PROFIL_CUR_SECS C
     WHERE C.REF_ID   = VAR_REF_ID_REPORT
      AND
       C.NAME     = T.NAME
    );

  COMMIT;
  SELECT
   PKG_CURRENCY_SENSIVITY.FNC_GET_QUERY_CUR_SENS(VAR_REF_ID_REPORT)
  INTO
   VAR_QUERY_CUR
  FROM DUAL;

 END PRC_CUR_SENSIVITY_REPORT;

