 PROCEDURE PRC_DELETE_REPORT (
  INPAR_ID   IN NUMBER
 ,OUTPAR     OUT VARCHAR2
 ) AS
  VAR_CATEGORY   VARCHAR2(200);
 BEGIN
  SELECT
   CATEGORY
  INTO
   VAR_CATEGORY
  FROM TBL_REPORT
  WHERE H_ID   = INPAR_ID;

  IF
   ( UPPER(VAR_CATEGORY) != 'SENSITIVE' )
  THEN   /* hameye gozareshha be joz gozareshat "SENSITIVE"*/
   UPDATE TBL_REPORT
    SET
     STATUS = 0
   WHERE H_ID   = INPAR_ID;

   OUTPAR   := NULL;
  END IF;

 EXCEPTION
  WHEN NO_DATA_FOUND THEN
   SELECT
    CATEGORY
   INTO
    VAR_CATEGORY
   FROM TBL_REPORT
   WHERE ID   = INPAR_ID;

   IF
    ( UPPER(VAR_CATEGORY) = 'SENSITIVE' )
   THEN
    DELETE FROM TBL_REPORT WHERE ID   = INPAR_ID;

    DELETE FROM TBL_REP_DETAIL_PROFIL_CUR_SECS WHERE REF_ID   = INPAR_ID;

   END IF;

 END PRC_DELETE_REPORT;
