CREATE OR REPLACE PACKAGE BODY "PRAGG"."PKG_LEDGER_REPORT_MAP" AS
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/
  /*
  Package Programmers Name:  morteza.sahi & Navid.Sedigh
  Editor Name: 
  Release Date/Time:
  Edit Name: 
  Version: 1
  Category:2
  Description: 
  */
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_GI_CALC (
  INPAR_ID           IN NUMBER
 ,INPAR_REF_REPORT   IN NUMBER
 ,INPAR_CUR_ID       IN NUMBER
 ,INPAR_DATE         IN DATE
 ) RETURN VARCHAR2 AS
  VAR         CLOB;
  VAR2        CLOB;
  VAR3        CLOB;
  ID_BRANCH   NUMBER;
  VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(INPAR_DATE,'J');
 BEGIN
  SELECT
   REF_BRN_PROFILE
  INTO
   ID_BRANCH
  FROM TBL_REPORT
  WHERE ID   = INPAR_REF_REPORT;

  SELECT
   '
  SELECT
   REPLACE(
    WMSYS.WM_CONCAT(V)
   ,'',''
   ,''''
   )
   FROM (
    SELECT
     ABS(DG.BALANCE) ||
     A.SPLIT_SING AS V
    FROM (
      WITH T AS (
       SELECT
        REPLACE(FORMULA||''+'','','','''') STR
       FROM TBL_LEDGER_REPORT_MAP
       WHERE id   = ' ||
   INPAR_ID ||
   '   ) SELECT
       REGEXP_SUBSTR(
        STR
       ,''[0-9]+''
       ,1
       ,LEVEL
       ) SPLIT_VALUES
      ,REGEXP_SUBSTR(
        STR
       ,''[^0-9]+''
       ,1
       ,LEVEL
       ) SPLIT_SING
      ,LEVEL AS LEV
      FROM T
      CONNECT BY
       LEVEL <= (
        SELECT
         LENGTH(REPLACE(STR,''-'',NULL) )
        FROM T
       )
     ) A
    ,TBL_LEDGER_BRANCH PARTITION ('||VAR_PARTITION||')  DG
    WHERE DG.LEDGER_CODE   = A.SPLIT_VALUES
     AND
      SPLIT_VALUES IS NOT NULL
     AND
      DG.REF_CUR_ID    = ' ||
   INPAR_CUR_ID ||
   '
     AND
      trunc(DG.EFF_DATE)                                          = trunc(TO_DATE(''' ||
   INPAR_DATE ||
   '''))
       and DG.REF_BRANCH in (' ||
   FNC_PRIVATE_CREATE_QUERY('TBL_BRANCH',ID_BRANCH) ||
   ')
    ORDER BY A.LEV
   )'
  INTO
   VAR
  FROM DUAL;
 -- RETURN VAR ;
  EXECUTE IMMEDIATE VAR INTO
   VAR3;
  SELECT
   CASE
    WHEN SUBSTR(
     TO_CHAR(VAR3)
    ,-1
    ) IN (
     '-','+'
    ) THEN VAR3 ||
    '0'
    ELSE VAR3
   END
  INTO
   VAR
  FROM DUAL;

  EXECUTE IMMEDIATE 'select ' ||
  NVL(TO_CHAR(VAR),0) ||
  ' from dual' INTO
   VAR2;

  RETURN VAR2;
 END FNC_GI_CALC;
/*=============================================================*/
/*=============================================================*/
/*=============================================================*/

 PROCEDURE PRC_GI_MAP (
  INPAR_NAME            VARCHAR2
 ,INPAR_DESCRIPTION     VARCHAR2
 ,INPAR_FORMUL          clob
 ,INPAR_STANDARD_TYPE   VARCHAR2
 ,OUTPAR_ID             OUT VARCHAR2
 ) AS
 VAR_COUNT            NUMBER;
var_j                NUMBER;
var_i                NUMBER;
VAR_ID               NUMBER;
VAR_REPORT_ID_UPDATE NUMBER;
VAR_NAME             VARCHAR2(1000);
VAR_DESCRIPTION      VARCHAR2(2000);
VAR_CREATED_DATE     DATE;
VAR_STANDARD_TYPE    VARCHAR2(20);

 BEGIN

 var_j :=1;
 var_i:=1;

  SELECT
   COUNT(DISTINCT NAME)
  INTO
   VAR_COUNT
  FROM TBL_LEDGER_REPORT_MAP
  WHERE NAME   = ( UPPER(INPAR_NAME) );

  IF
   ( VAR_COUNT = 0 AND INPAR_NAME <> ' ' )
  THEN

select TBL_LEDGER_REPORT_MAP_SEQ1.nextval INTO VAR_ID from dual;
      loop

       INSERT INTO TBL_LEDGER_REPORT_MAP (
    NAME
   ,DESCRIPTION
   ,CREATED_DATE
   ,STANDARD_TYPE
   ,FORMULA
   ,REPORT_MAP_ID
   ,PRIORITY
   ) VALUES (
    PKG_LEDGER_REPORT_MAP.FNC_AD_FARSIVALIDATE(UPPER(INPAR_NAME) )
   ,INPAR_DESCRIPTION
   ,SYSDATE
   ,INPAR_STANDARD_TYPE
   ,to_char(substr(INPAR_FORMUL,var_j, 4000))
   ,VAR_ID+1,
   var_i
   );

   COMMIT;


  exit when nvl(LENGTHB((TO_CHAR(SUBSTR(INPAR_FORMUL,var_j, 4000)))),0) < 4000;
   var_j:= var_j+4000;
   var_i := var_i+1;
    end loop;

SELECT REPORT_MAP_ID
INTO OUTPAR_ID
FROM TBL_LEDGER_REPORT_MAP
WHERE id IN
  ( SELECT MAX(ID) FROM TBL_LEDGER_REPORT_MAP
  );





end if;

  IF ( VAR_COUNT = 1 AND INPAR_NAME <> ' ' ) THEN

SELECT MAX(REPORT_MAP_ID),
  MAX(NAME),
  MAX(DESCRIPTION),
  MAX(CREATED_DATE),
  MAX(STANDARD_TYPE) INTO VAR_REPORT_ID_UPDATE,VAR_NAME,VAR_DESCRIPTION,VAR_CREATED_DATE,VAR_STANDARD_TYPE FROM TBL_LEDGER_REPORT_MAP WHERE NAME   = ( UPPER(INPAR_NAME) );

 DELETE FROM TBL_LEDGER_REPORT_MAP WHERE REPORT_MAP_ID = VAR_REPORT_ID_UPDATE;
 COMMIT;

  var_i:=1;
        loop



     INSERT
INTO TBL_LEDGER_REPORT_MAP
  (
    NAME ,
    DESCRIPTION ,
    CREATED_DATE ,
    EDITED_DATE,
    STANDARD_TYPE ,
    FORMULA ,
    REPORT_MAP_ID ,
    PRIORITY
  )
  VALUES
  (
    VAR_NAME ,
    VAR_DESCRIPTION ,
    VAR_CREATED_DATE ,
    SYSDATE,
    VAR_STANDARD_TYPE ,
    TO_CHAR(SUBSTR(INPAR_FORMUL,var_j, 4000)) ,
   VAR_REPORT_ID_UPDATE,
    var_i
  ); 








--   UPDATE TBL_LEDGER_REPORT_MAP
--    SET
--     DESCRIPTION = INPAR_DESCRIPTION
--    ,EDITED_DATE = SYSDATE
--    ,FORMULA = substr(INPAR_FORMUL,var_j, 4000)
--   WHERE NAME   = ( UPPER(INPAR_NAME) )
--   and PRIORITY =var_i ;
--      

   COMMIT;


  exit when nvl(LENGTHB((TO_CHAR(SUBSTR(INPAR_FORMUL,var_j, 4000)))),0) < 4000;
    var_j:= var_j+4000;
   var_i := var_i+1;


    end loop;


    OUTPAR_ID := VAR_REPORT_ID_UPDATE ;

   COMMIT;

  end if;

 END PRC_GI_MAP;
/*=============================================================*/
/*=============================================================*/
/*=============================================================*/

 FUNCTION FNC_AD_FARSIVALIDATE ( STR NVARCHAR2 ) RETURN NVARCHAR2 AS
  TEMPSTR   NVARCHAR2(1000);
 BEGIN
  TEMPSTR   := STR;
  TEMPSTR   := REPLACE(
   TEMPSTR
  ,UNISTR('\064a')
  ,UNISTR('\06cc')
  );
  TEMPSTR   := REPLACE(
   TEMPSTR
  ,UNISTR('\0643')
  ,UNISTR('\06a9')
  );
  RETURN TEMPSTR;
 END FNC_AD_FARSIVALIDATE;
