/*=============================================================*/
/*=============================================================*/

 PROCEDURE PRC_DELETE_PROFILE (
  INPAR_ID    IN VARCHAR2
 ,OUTPAR_ID   OUT VARCHAR2
 )
  AS
 BEGIN
  OUTPAR_ID   := 0;
  DELETE FROM TBL_LEDGER_REPORT_MAP WHERE REPORT_MAP_ID   = INPAR_ID;

  COMMIT;
 END PRC_DELETE_PROFILE;
