 FUNCTION FNC_GET_QUERY_FORMULA ( INPAR_ID IN VARCHAR2 ) RETURN clob AS
  VAR   clob;
 BEGIN
  VAR   := 'SELECT  min(REPORT_MAP_ID) "id",
  min(name) "name",
  min(description) "description",
  min(CREATED_DATE) "createdDate",
  min(EDITED_DATE) "editDate",
  min(STANDARD_TYPE) "constant",rtrim(xmlagg(XMLELEMENT(e,FORMULA).EXTRACT(''//text()'')
    ).GetClobVal(),'','') "formula"
    FROM ( SELECT * FROM TBL_LEDGER_REPORT_MAP
    WHERE REPORT_MAP_ID ='
|| INPAR_ID ||' 
ORDER BY PRIORITY)';
  RETURN VAR;
 END FNC_GET_QUERY_FORMULA;

/*=============================================================*/
/*=============================================================*/
/*=============================================================*/

 FUNCTION FNC_GET_QUERY_FORMULA_PROFILE ( INPAR_TYPE IN VARCHAR2 ) RETURN VARCHAR2
  AS
 BEGIN
  RETURN 'SELECT MIN(REPORT_MAP_ID)     AS "id",
  MIN(NAME)        AS "name",
  MIN(description) AS "des"
FROM TBL_LEDGER_report_map
WHERE upper(standard_type) =upper('''
|| INPAR_TYPE || ''')
GROUP BY REPORT_MAP_ID
ORDER BY 1 DESC';


 END FNC_GET_QUERY_FORMULA_PROFILE;
