 FUNCTION FNC_AD_FARSIVALIDATE ( STR NVARCHAR2 ) RETURN NVARCHAR2 AS
  TEMPSTR   NVARCHAR2(1000);
 BEGIN
  TEMPSTR   := STR;
  TEMPSTR   := REPLACE(
   TEMPSTR
  ,UNISTR('\064a')
  ,UNISTR('\06cc')
  );
  TEMPSTR   := REPLACE(
   TEMPSTR
  ,UNISTR('\0643')
  ,UNISTR('\06a9')
  );
  RETURN TEMPSTR;
 END FNC_AD_FARSIVALIDATE;
