 PROCEDURE PRC_CAR_FINAL_REPORT (
  INPAR_REF_REPORT   IN NUMBER
  ,INPAR_REF_REPREQ   IN NUMBER
 )
  AS
 BEGIN

-- DELETE FROM TBL_CAR_FINAL_REPORT
--  WHERE ref_report = INPAR_REF_REPORT; 
--  commit; 
--  
  FOR I IN (
   SELECT
    TCP.ID
   ,TC.ID AS ID_DATE
   ,TCP.REF_REP_ID
   ,TC.CAR_DATE
   ,TCP.NAME
   ,TCP.PROFILE_ID
   ,TCP.PERCENT * TCP.PERCENT2 as PERCENT
   ,TCP.TYPE
   FROM TBL_CAR_DATE TC
   ,    TBL_CAR_REP_PROFILE_DETAIL TCP
   WHERE TC.REF_REP_ID    = TCP.REF_REP_ID
    AND
     TCP.REF_REP_ID   = INPAR_REF_REPORT
  ) LOOP
   IF
    ( I.TYPE IN (
      1,2
     )
    )
   THEN
    INSERT INTO TBL_CAR_FINAL_REPORT (
     REF_CAR_REP_DETAIL
    ,REF_CAR_DATE
    ,BALANCE
    ,REF_REPORT
    ,REPORT_DATE
    ,NAME
    ,TYPE
    ,PERCENT
    ,REF_REPREQ
    ) VALUES (
     I.ID
    ,I.ID_DATE
    ,to_char(PKG_CAR.FNC_CAR_GI_CALC(
      I.PROFILE_ID
     ,I.CAR_DATE
     ))
    ,INPAR_REF_REPORT
    ,I.CAR_DATE
    ,I.NAME
    ,I.TYPE
    ,I.PERCENT
    ,INPAR_REF_REPREQ
    );

    COMMIT;
   ELSE
    INSERT INTO TBL_CAR_FINAL_REPORT (
     REF_CAR_REP_DETAIL
    ,REF_CAR_DATE
    ,BALANCE
    ,REF_REPORT
    ,REPORT_DATE
    ,NAME
    ,TYPE
    ,PERCENT
     ,REF_REPREQ
    ) VALUES (
     I.ID
    ,I.ID_DATE
    ,to_char(PKG_CAR.FNC_CAR_GI_CALC(
      I.PROFILE_ID
     ,I.CAR_DATE
     )) * I.PERCENT / 100
    ,INPAR_REF_REPORT
    ,I.CAR_DATE
    ,I.NAME
    ,I.TYPE
    ,I.PERCENT
    ,INPAR_REF_REPREQ
    );

    COMMIT;
   END IF;

   COMMIT;
   UPDATE TBL_CAR_FINAL_REPORT
    SET
     BALANCE = (
      SELECT  /*+   PARALLEL(auto) */
       SUM(CURRENT_AMOUNT)
      FROM AKIN.TBL_LOAN
      WHERE LON_ID IN (
        SELECT
         REF_LON_ID
        FROM AKIN.TBL_LOAN_PAYMENT
        WHERE DUE_DATE >= I.CAR_DATE + 1800
        GROUP BY
         REF_LON_ID
       )
     )
   WHERE REPORT_DATE   = I.CAR_DATE
    AND
     TYPE          = 4;

   COMMIT;
  END LOOP;

  END PRC_CAR_FINAL_REPORT;
