 PROCEDURE PRC_CAR_DATE (
  INPAR_INSERT_OR_UPDATE   IN NUMBER  /*if inpar_insert_or_update-1==> insert   else  ==>update*/
 ,INPAR_REF_REP_ID         IN NUMBER
 ,INPAR_CAR_DATE           IN VARCHAR2
 ,OUTPUT                   OUT VARCHAR2
 )
  AS
 BEGIN
 /*
  Programmer Name: sobhan
  Release Date/Time:1396/07/15
  Version: 1.0
  Category:
  Description:
  */
  IF
   ( INPAR_INSERT_OR_UPDATE =-1 )
  THEN
   INSERT INTO TBL_CAR_DATE ( REF_REP_ID,CAR_DATE ) ( SELECT
    INPAR_REF_REP_ID
   ,TO_DATE(D,'yyyy/mm/dd','nls_calendar=persian')
   FROM (
     SELECT
      INPAR_REF_REP_ID
     ,REGEXP_SUBSTR(
       INPAR_CAR_DATE
      ,'[^#]+'
      ,1
      ,LEVEL
      ) AS D
     FROM DUAL
     CONNECT BY
      REGEXP_SUBSTR(
       INPAR_CAR_DATE
      ,'[^#]+'
      ,1
      ,LEVEL
      ) IS NOT NULL
    )
   );

  ELSE
   DELETE FROM TBL_CAR_DATE WHERE REF_REP_ID   = INPAR_REF_REP_ID;

   INSERT INTO TBL_CAR_DATE ( REF_REP_ID,CAR_DATE ) ( SELECT
    INPAR_REF_REP_ID
   ,TO_DATE(D,'yyyy/mm/dd','nls_calendar=persian')
   FROM (
     SELECT
      INPAR_REF_REP_ID
     ,REGEXP_SUBSTR(
       INPAR_CAR_DATE
      ,'[^#]+'
      ,1
      ,LEVEL
      ) AS D
     FROM DUAL
     CONNECT BY
      REGEXP_SUBSTR(
       INPAR_CAR_DATE
      ,'[^#]+'
      ,1
      ,LEVEL
      ) IS NOT NULL
    )
   );

  END IF;

  OUTPUT   := NULL;
 END PRC_CAR_DATE;
