 FUNCTION FNC_CAR_GI_CALC (
  INPAR_ID     IN NUMBER
 ,INPAR_DATE   IN DATE
 ) RETURN clob AS
  VAR    CLOB;
  VAR2   CLOB;
  VAR3   CLOB;
  VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(INPAR_DATE,'J');

 BEGIN
  SELECT
   '
  SELECT /*+   PARALLEL(auto) */ 
   REPLACE(
    WMSYS.WM_CONCAT(V)
   ,'',''
   ,''''
   )
   FROM (
    SELECT /*+   PARALLEL(auto) */ 
     ''(''||DG.BALANCE ||'')''||
     A.SPLIT_SING AS V
    FROM (  WITH T AS (
    select /*+   PARALLEL(auto) */   
     fnc_agg_formula_for_report_map (' ||
   INPAR_ID ||
   ' ) as STR from dual
   ) SELECT  
       REGEXP_SUBSTR(
        STR
       ,''[0-9]+''
       ,1
       ,LEVEL
       ) SPLIT_VALUES
      ,REGEXP_SUBSTR(
        STR
       ,''[^0-9]+''
       ,1
       ,LEVEL
       ) SPLIT_SING
      ,LEVEL AS LEV
      FROM T
      CONNECT BY
       LEVEL <= (
        SELECT
         LENGTH(REPLACE(STR,''-'',NULL) )
        FROM T
       )
     ) A
    ,TBL_LEDGER_archive PARTITION ('||VAR_PARTITION||') DG
    WHERE DG.LEDGER_CODE   = to_char(A.SPLIT_VALUES)
     AND
      SPLIT_VALUES IS NOT NULL
        AND
      trunc(DG.EFF_DATE)                                          = trunc(TO_DATE(''' ||
   INPAR_DATE ||
   ''')))
 '
  INTO
   VAR
  FROM DUAL;
 -- RETURN VAr;
  EXECUTE IMMEDIATE VAR INTO
   VAR3;
  SELECT
   CASE
    WHEN to_char(SUBSTR(
    VAR3
    ,-1
    )) IN (
     '-','+'
    ) THEN VAR3 ||
    '0'
    ELSE VAR3
   END
  INTO
   VAR
  FROM DUAL;


--var2:='select ' ||
-- case when var is null then '0' else var end ||
--  ' from dual';

DBMS_OUTPUT.PUT_LINE(var);

  EXECUTE IMMEDIATE 'select ' ||
 case when var is null then '0' else var end ||
  ' from dual' INTO
   VAR2;

  --RETURN (to_number(VAR2));
  RETURN VAR2;
 END FNC_CAR_GI_CALC;
