/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CAR_ALL_REPORT ( INPAR_ID IN NUMBER ) RETURN VARCHAR2 AS
  VAR2   VARCHAR2(3000);
 BEGIN
  VAR2   := 'SELECT ID as "id",
  NAME as "name",
  DES as "des",
  CREATE_DATE as "createDate",
  REF_USER as "refUser",
  STATUS as "status",
  CATEGORY as "category"
FROM TBL_REPORT 
where id = '
|| INPAR_ID || ' and upper(category) = ''CAR''';
  RETURN VAR2;
 END FNC_CAR_ALL_REPORT;
/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CAR_FINAL_REPORT (
  INPAR_REF_REPREQ   IN NUMBER
 ,INPAR_TYPE     IN NUMBER
 ) RETURN VARCHAR2 AS
  VAR           VARCHAR2(10000);
  VAR_TIMING    VARCHAR2(1000);
  VAR_TIMING1   VARCHAR2(1000);
    VAR_TIMING3    VARCHAR2(1000);
  VAR_TIMING4   VARCHAR2(1000);
  var_report number;
 BEGIN

 select ref_report_id into var_report from TBL_REPREQ where id = INPAR_REF_REPREQ;
  SELECT
   WMSYS.WM_CONCAT( (
    SELECT
     ID ||
     ' AS "x' ||
     REPLACE(ID,' ','_') ||
     '"'
    FROM DUAL
   ) )
  INTO
   VAR_TIMING
  FROM TBL_CAR_DATE
  WHERE REF_REP_ID   = var_report;

  SELECT
   WMSYS.WM_CONCAT( (
    SELECT
     '  "x' ||
     REPLACE(ID,' ','_') ||
     '"'
    FROM DUAL
   ) )
  INTO
   VAR_TIMING1
  FROM TBL_CAR_DATE
  WHERE REF_REP_ID   = var_report;



  SELECT
   WMSYS.WM_CONCAT( (
    SELECT
     '  sum("x' ||
     REPLACE(ID,' ','_') ||
     '") as    "x' ||
     REPLACE(ID,' ','_') ||
     '"'
    FROM DUAL
   ) )
  INTO
   VAR_TIMING4
  FROM TBL_CAR_DATE
  WHERE REF_REP_ID   = var_report;

  IF
   INPAR_TYPE = 4
  THEN
   VAR   := '
 SELECT name  as "des",' ||
   VAR_TIMING1 ||
   ',type as "type" FROM
(
SELECT
    REF_CAR_DATE,
    BALANCE,
    NAME,type
FROM
    TBL_CAR_FINAL_REPORT
    where  REF_REPREQ =' ||
   INPAR_REF_REPREQ ||
   '
    and TBL_CAR_FINAL_REPORT.type in (1,2,4)
)
PIVOT 
(
  sum(BALANCE)
  FOR (REF_CAR_DATE)
  IN ( ' ||
   VAR_TIMING ||
   ' ) 
)
union    
    SELECT  name  as "des",' ||
   VAR_TIMING1 ||
   ',type as "type"  FROM
(
SELECT
    max(REF_CAR_DATE) as REF_CAR_DATE,
    sum(BALANCE) as BALANCE,
    ''      '' as NAME,
      max(type) as type
FROM
    TBL_CAR_FINAL_REPORT
    where  REF_REPREQ =' ||
   INPAR_REF_REPREQ ||
   '
    and TBL_CAR_FINAL_REPORT.type   in (3)
group by REPORT_DATE)
PIVOT 
(
  sum(BALANCE)
  FOR (REF_CAR_DATE)
  IN ( ' ||
   VAR_TIMING ||
   ' ) 

)
order by "type"';
  END IF;

  IF INPAR_TYPE IN (
    3
   )
  THEN
   VAR   := '    SELECT max(name)  as "des",percent,' ||
   VAR_TIMING4 ||
   ', 1 "indent" FROM
(

SELECT
REF_CAR_DATE,
    BALANCE,
    NAME,
    percent
FROM
    TBL_CAR_FINAL_REPORT
    where type = 3 and REF_REPREQ = ' ||
   INPAR_REF_REPREQ ||
   ')
PIVOT 
(
  sum(BALANCE)
  FOR (REF_CAR_DATE)
  IN ( ' ||
   VAR_TIMING ||
   ' ) 

) group by percent ';
  END IF;

  IF INPAR_TYPE IN (
    1
   )
  THEN
   VAR   := '    SELECT name  as "des",' ||
   VAR_TIMING1 ||
   ' FROM
(

SELECT
REF_CAR_DATE,
    BALANCE,
    NAME
FROM
    TBL_CAR_FINAL_REPORT
    where type = ' ||
   INPAR_TYPE ||
   ' and REF_REPREQ = ' ||
   INPAR_REF_REPREQ ||
   ')
PIVOT 
(
  sum(BALANCE)
  FOR (REF_CAR_DATE)
  IN ( ' ||
   VAR_TIMING ||
   ' ) 

)';
  END IF;

  IF INPAR_TYPE IN (
    2
   )
  THEN
   VAR   := '    SELECT name  as "des",' ||
   VAR_TIMING1 ||
   ' FROM
(

SELECT
REF_CAR_DATE,
    BALANCE,
    NAME
FROM
    TBL_CAR_FINAL_REPORT
