   pragma autonomous_transaction;
 BEGIN
 pkg_niim.prc_niim_update_gi_calc(INPAR_REPORT);
  OUTPUT   := 'select
   id as "id",
  NAME as "infoGroup",
  TYPE as "type",
  TITLE as "title",
