 FUNCTION FNC_NPL_GI_CALC ( INPAR_ID IN varchar2 ) RETURN VARCHAR2 AS
  VAR    CLOB;
  VAR2   CLOB;
  VAR3   CLOB;
 BEGIN
  SELECT
   '
  SELECT
   REPLACE(
    WMSYS.WM_CONCAT(V)
   ,'',''
   ,''''
   )
   FROM (
    SELECT
     ABS(DG.BALANCE) ||
     A.SPLIT_SING AS V
    FROM (
      WITH T AS (
       SELECT
        REPLACE(FORMULA||''+'','','','''') STR
       FROM TBL_LEDGER_REPORT_MAP
       WHERE id   = '
|| INPAR_ID || '   ) SELECT
       REGEXP_SUBSTR(
        STR
       ,''[0-9]+''
       ,1
       ,LEVEL
       ) SPLIT_VALUES
      ,REGEXP_SUBSTR(
        STR
       ,''[^0-9]+''
       ,1
       ,LEVEL
       ) SPLIT_SING
      ,LEVEL AS LEV
      FROM T
      CONNECT BY
       LEVEL <= (
        SELECT
         LENGTH(REPLACE(STR,''-'',NULL) )
        FROM T
       )
     ) A
    ,TBL_LEDGER_archive DG
    WHERE DG.LEDGER_CODE   = A.SPLIT_VALUES
     AND
      SPLIT_VALUES IS NOT NULL
        AND
      trunc(DG.EFF_DATE)                                          = (select trunc(max(eff_date)) from  TBL_LEDGER_archive))
 '
  INTO
   VAR
  FROM DUAL;

  EXECUTE IMMEDIATE VAR INTO
   VAR3;
  SELECT
   CASE
    WHEN SUBSTR(
     TO_CHAR(VAR3)
    ,-1
    ) IN (
     '-','+'
    ) THEN VAR3 ||
    '0'
    ELSE VAR3
   END
  INTO
   VAR
  FROM DUAL;

  EXECUTE IMMEDIATE 'select ' ||
  NVL(TO_CHAR(VAR),0) ||
  ' from dual' INTO
   VAR2;

  RETURN abs(TO_number(VAR2));
 END FNC_NPL_GI_CALC;
