CREATE OR REPLACE PACKAGE BODY "PRAGG"."PKG_CONC" AS
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/
  /*
  Package Programmers Name:  morteza.sahi & Navid.Sedigh
  Editor Name: 
  Release Date/Time:
  Edit Name: 
  Version: 1
  Category:2
  Description: 
  */
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/

 PROCEDURE PRC_CONC_REP_PROFILE_REPORT
  AS
 BEGIN
  EXECUTE IMMEDIATE 'truncate table TBL_CONC_RPORT_DETAIL';

  INSERT /*+ APPEND  PARALLEL(auto) */ INTO TBL_CONC_RPORT_DETAIL (
   CUSTOMER_NUMBER
  ,REF_BRANCH
  ,BALANCE
  ,BRANCH_NAME
  ,REF_CITY
  ,CITY_NAME
  ,REF_STATE
  ,STATE_NAME
  ,REGION_NAME
  ,REGION_ID
  ,MODALITY_TYPE
  ) SELECT  /*+   PARALLEL(auto) */
   A.REF_CUSTOMER
  ,A.REF_BRANCH
  ,A.BALANCE
  ,B.NAME
  ,B.REF_CTY_ID
  ,B.CITY_NAME
  ,B.REF_STA_ID
  ,B.STA_NAME
  ,B.REGION_NAME
  ,B.REGION_ID
  ,A.MODALITY_TYPE
  FROM (
    SELECT  /*+   PARALLEL(auto) */
     REF_CUSTOMER
    ,REF_BRANCH
    ,SUM(BALANCE) AS BALANCE
    ,case when max(REF_DEPOSIT_TYPE) in (43,44,540,541,542,543,544,556,557,559,560)then 4 else MAX(MODALITY_TYPE) end AS MODALITY_TYPE
    FROM AKIN.TBL_DEPOSIT
    GROUP BY
     REF_CUSTOMER
    ,REF_BRANCH
   ) A
  ,    TBL_BRANCH B
  WHERE A.REF_BRANCH   = B.BRN_ID;

 END PRC_CONC_REP_PROFILE_REPORT;
/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CONC_STATE_REPORT RETURN VARCHAR2 AS
  VAR_SUM_BALANCE   NUMBER;
 BEGIN
  SELECT
   SUM(BALANCE)
  INTO
   VAR_SUM_BALANCE
  FROM TBL_CONC_RPORT_DETAIL;

  RETURN 'select ref_state "hc-key",sum(balance) "value",max(state_name)"name",to_char(0||round(sum(balance)/' || VAR_SUM_BALANCE || ',5)) as "percent" from tbl_conc_rport_detail group by ref_state order by ref_state'
;
 END FNC_CONC_STATE_REPORT;
/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CONC_MAIN_REPORT RETURN clob AS
  VAR_COUNT         NUMBER;
  VAR_SUM_BALANCE   NUMBER;
  VAR_PERCENT_100   NUMBER;
 BEGIN

   return 'WITH tmp AS
                (SELECT 
                  customer_number,
                  balance,
                  count(balance) over (partition BY 1) total_count,
                  sum(balance) over (partition BY 1) total_balance,
                  ceil(((row_number() over (partition BY 1 order by balance DESC)/COUNT(balance) over (partition BY 1)))*10) deck
                FROM tbl_conc_rport_detail
                )
              SELECT /*+ parallel(auto) */
                deck "1",
                SUM(SUM(balance)) over (partition BY 1 order by deck) "value",
                SUM(count(balance)) over (partition BY 1 order by deck) "count",
                 0||to_char(round(SUM(SUM(balance)) over (partition BY 1 order by deck) / max(total_balance), 5)) "percent"
              FROM tmp
              GROUP BY deck
              ORDER BY deck';

 END FNC_CONC_MAIN_REPORT;

/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CONC_STATE_DETAIL_REPORT ( INPAR_STATE IN NUMBER ) RETURN clob AS
  VAR_COUNT         NUMBER;
  VAR_SUM_BALANCE   NUMBER;
  VAR_PERCENT_100   NUMBER;
 BEGIN
  SELECT /*+ parallel(auto) */
   COUNT(DISTINCT CUSTOMER_NUMBER)
  INTO
   VAR_COUNT
  FROM TBL_CONC_RPORT_DETAIL
  WHERE REF_STATE   = INPAR_STATE;

  SELECT /*+ parallel(auto) */
   SUM(BALANCE)
  INTO
   VAR_SUM_BALANCE
  FROM TBL_CONC_RPORT_DETAIL
  WHERE REF_STATE   = INPAR_STATE;

  SELECT /*+ parallel(auto) */
   TO_CHAR(ROUND(
    SUM(BALANCE) / VAR_SUM_BALANCE
   ,5
   ) * 100)
  INTO
   VAR_PERCENT_100
  FROM (
    SELECT /*+ parallel(auto) */
     CUSTOMER_NUMBER
    ,BALANCE
    FROM (
      SELECT /*+ parallel(auto) */
       CUSTOMER_NUMBER
      ,SUM(BALANCE) AS BALANCE
      FROM TBL_CONC_RPORT_DETAIL
      WHERE REF_STATE   = INPAR_STATE
      GROUP BY
       CUSTOMER_NUMBER
     )
    ORDER BY BALANCE DESC
   )
  WHERE ROWNUM <= 100;


 return 'WITH tmp AS
                (SELECT 
                  customer_number,
                  balance,
                  count(balance) over (partition BY 1) total_count,
                  sum(balance) over (partition BY 1) total_balance,
                  ceil(((row_number() over (partition BY 1 order by balance DESC)/COUNT(balance) over (partition BY 1)))*10) deck
                FROM tbl_conc_rport_detail
                where ref_state = '||  INPAR_STATE  ||'
                )
              SELECT /*+ parallel(auto) */
                deck "1",
                deck*10 "ha",
                SUM(SUM(balance)) over (partition BY 1 order by deck) "value",
                SUM(count(balance)) over (partition BY 1 order by deck) "count",
                (round(SUM(SUM(balance)) over (partition BY 1 order by deck) / max(total_balance), 5)) "percent",
                '|| VAR_PERCENT_100   || ' "top100"
              FROM tmp
              GROUP BY deck
              ORDER BY deck';

 END FNC_CONC_STATE_DETAIL_REPORT;

/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CONC_BRANCH_REPORT RETURN VARCHAR2 AS
  VAR_SUM_BALANCE   NUMBER;
 BEGIN
  SELECT
   SUM(BALANCE)
  INTO
   VAR_SUM_BALANCE
  FROM TBL_CONC_RPORT_DETAIL;

  RETURN 'SELECT
 to_number(REF_BRANCH)"id"
 ,BALANCE "value"
 ,NAME as "name"
 ,to_char(0||round(BALANCE/' || VAR_SUM_BALANCE || ',6)) "percent"
FROM (
  SELECT
   REF_BRANCH
  ,SUM(BALANCE) AS BALANCE
  ,MAX(TBL_BRANCH.NAME) AS NAME
  FROM TBL_CONC_RPORT_DETAIL
  ,  TBL_BRANCH
  WHERE TBL_BRANCH.BRN_ID   = TBL_CONC_RPORT_DETAIL.REF_BRANCH
  GROUP BY
   REF_BRANCH
 )
ORDER BY BALANCE DESC'
;
 END FNC_CONC_BRANCH_REPORT;
/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_PAGING_QUERY (
/*======YEK QUERY V PAGE-SIZE V PAGE-NUMBER MIGIRE VA DADEHASHO PAS MIDE*/
  INPAR_PAGE_SIZE     IN NUMBER
 ,INPAR_PAGE_NUMBER   IN NUMBER
 ) RETURN CLOB AS

  LOC_QUERY     CLOB;
  LOC_LOW       NUMBER := ( INPAR_PAGE_NUMBER - 1 ) * INPAR_PAGE_SIZE + 1;
  LOC_UP        NUMBER := INPAR_PAGE_NUMBER * INPAR_PAGE_SIZE;
  INPAR_QUERY   CLOB := PKG_CONC.FNC_CONC_BRANCH_REPORT ();
  begin
 LOC_QUERY := 'SELECT * FROM (
                              SELECT ROWNUM "", t.*
                              FROM (' || INPAR_QUERY ||')T)
                              WHERE  "" BETWEEN ' || LOC_LOW || ' AND ' ||LOC_UP;
 RETURN LOC_QUERY;
 END FNC_PAGING_QUERY;

/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_PAGE_NUMBER ( INAPR_PAGE_SIZE IN NUMBER ) RETURN VARCHAR2 AS
  LOC_QUERY     VARCHAR2(4000);
  LOC_CNT       VARCHAR2(4000);
  INPAR_QUERY   VARCHAR2(32000);
 BEGIN
  INPAR_QUERY   := PKG_CONC.FNC_CONC_BRANCH_REPORT ();
  LOC_QUERY     := 'SELECT FLOOR((COUNT(*)/' ||
  INAPR_PAGE_SIZE ||
  ')+1)  FROM (' ||
  INPAR_QUERY ||
  ')';
  EXECUTE IMMEDIATE LOC_QUERY INTO
   LOC_CNT;
  RETURN LOC_CNT;
 END FNC_PAGE_NUMBER;

/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/
FUNCTION FNC_GET_TOP100_CUSTOMER (reg IN NUMBER) RETURN VARCHAR2 AS

  BEGIN
   IF (reg IS NULL) THEN
      RETURN ' 

         with c as ( select  "customerNumber","balance",MODALITY_TYPE from (
 SELECT /*+   PARALLEL(auto) */
 CUSTOMER_NUMBER "customerNumber"
 ,BALANCE "balance"
 ,MODALITY_TYPE
FROM TBL_CONC_RPORT_DETAIL
     ORDER BY BALANCE DESC
 ) where ROWNUM<101)
, cn as (select /*+   PARALLEL(auto) */  CUS_ID,NAME||'' ''||FAMILY as name from tbl_customer )
select /*+   PARALLEL(auto) */ c."customerNumber" ,c."balance" ,cn.name as "customerName",c.MODALITY_TYPE from c left join cn on c."customerNumber" = cn.CUS_ID
       ORDER BY c."balance" DESC 
      ' ;

   ELSe 
         RETURN ' 
         with c as (select  "customerNumber","balance",MODALITY_TYPE from (
 SELECT /*+   PARALLEL(auto) */
 CUSTOMER_NUMBER "customerNumber"
 ,BALANCE "balance"
 ,MODALITY_TYPE
FROM TBL_CONC_RPORT_DETAIL
where REGION_ID = '||reg||'
     ORDER BY BALANCE DESC
 ) where ROWNUM<101)
, cn as (select /*+   PARALLEL(auto) */  CUS_ID,NAME||'' ''||FAMILY as name from tbl_customer )
select /*+   PARALLEL(auto) */ c."customerNumber" ,c."balance" ,cn.name as "customerName", c.MODALITY_TYPE from c left join cn on c."customerNumber" = cn.CUS_ID
            ORDER BY c."balance" DESC 
         ' ;
    END IF;
  END FNC_GET_TOP100_CUSTOMER;

/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/
FUNCTION FNC_GET_region RETURN VARCHAR2 AS

  BEGIN
  return ' select "name","id" from 
 (select '''' "name" , null "id" from dual
union 
  select distinct region_name "name", region_id "id" from tbl_branch)
 order by 
 case
 when "id" is null then 000
 when "id"= 20 then 001
 else  "id"
 end';
  END FNC_GET_region;

/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/
FUNCTION FNC_GET_TOP100_CUSTOMER_detail (reg IN NUMBER) RETURN VARCHAR2 AS

  BEGIN


  return   'select  "customerNumber", "customerName",nvl("1",0) as "1",nvl("3",0) as "3",nvl("2",0) as "2",nvl("4",0) as "4" ,nvl("5",0) as "5" from (
  '||PKG_CONC.FNC_GET_TOP100_CUSTOMER(reg)||'

  )
            PIVOT
            (max("balance") FOR MODALITY_TYPE IN (1 as "1",2 as "2",3 as "3",4 as "4",5 as "5"))
            ORDER BY nvl("1",0)+nvl("2",0)+nvl("3",0)+nvl("4",0)+nvl("5",0) DESC';


  END FNC_GET_TOP100_CUSTOMER_detail;

/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/
FUNCTION FNC_GET_TOP100_CUSTOMER_all( REG IN NUMBER )  RETURN VARCHAR2 AS

  BEGIN

  if (reg = 1) then
  return   'WITH S AS (
 SELECT /*+   PARALLEL(auto) */
  CUSTOMER_NUMBER
 ,REGION_ID
 ,REGION_NAME
 ,BALANCE
 ,ROW_NUMBER() OVER(PARTITION BY
   REGION_ID
   ORDER BY
    BALANCE
   DESC
  ) AS ROWNUMS
 FROM TBL_CONC_RPORT_DETAIL
),C AS (
 SELECT /*+   PARALLEL(auto) */
  CUS_ID
 ,NAME ||
  '' '' ||
  FAMILY AS NAME
 FROM TBL_CUSTOMER
) SELECT
 REGION_NAME AS "regionName"
 ,C.NAME AS "customerName"
 ,S.CUSTOMER_NUMBER as "customerNumber",
 s.BALANCE as "balance"
FROM S
 ,    C
WHERE S.CUSTOMER_NUMBER   = C.CUS_ID
 AND
  S.ROWNUMS <= 100 ORDER BY REGION_NAME,balance DESC';
  else 
  return 'select  "customerNumber", "customerName","regionName",nvl("1",0) as "1",nvl("3",0) as "3",nvl("2",0) as "2",nvl("4",0) as "4" ,nvl("5",0) as "5" from (
WITH S AS (
 SELECT /*+   PARALLEL(auto) */
  CUSTOMER_NUMBER
 ,REGION_ID
 ,REGION_NAME
 ,BALANCE
 ,modality_type
 ,ROW_NUMBER() OVER(PARTITION BY
   REGION_ID
   ORDER BY
    BALANCE
   DESC
  ) AS ROWNUMS
 FROM TBL_CONC_RPORT_DETAIL
),C AS (
 SELECT /*+   PARALLEL(auto) */
  CUS_ID
 ,NAME ||
  '' '' ||
  FAMILY AS NAME
 FROM TBL_CUSTOMER
) SELECT
 REGION_NAME AS "regionName"
 ,C.NAME AS  "customerName"
 ,S.CUSTOMER_NUMBER as "customerNumber",
 s.BALANCE as "balance"
 ,modality_type
FROM S
 ,    C
WHERE S.CUSTOMER_NUMBER   = C.CUS_ID
 AND
  S.ROWNUMS <= 100 ORDER BY REGION_NAME,balance DESC

  )
            PIVOT
            (max("balance") FOR MODALITY_TYPE IN (1 as "1",2 as "2",3 as "3",4 as "4",5 as "5"))
            ORDER BY  "regionName",nvl("1",0)+nvl("2",0)+nvl("3",0)+nvl("4",0)+nvl("5",0) DESC';
  end if ;




  END FNC_GET_TOP100_CUSTOMER_all;

