 PROCEDURE PRC_CONC_REP_PROFILE_REPORT
  AS
 BEGIN
  EXECUTE IMMEDIATE 'truncate table TBL_CONC_RPORT_DETAIL';

  INSERT /*+ APPEND  PARALLEL(auto) */ INTO TBL_CONC_RPORT_DETAIL (
   CUSTOMER_NUMBER
  ,REF_BRANCH
  ,BALANCE
  ,BRANCH_NAME
  ,REF_CITY
  ,CITY_NAME
  ,REF_STATE
  ,STATE_NAME
  ,REGION_NAME
  ,REGION_ID
  ,MODALITY_TYPE
  ) SELECT  /*+   PARALLEL(auto) */
   A.REF_CUSTOMER
  ,A.REF_BRANCH
  ,A.BALANCE
  ,B.NAME
  ,B.REF_CTY_ID
  ,B.CITY_NAME
  ,B.REF_STA_ID
  ,B.STA_NAME
  ,B.REGION_NAME
  ,B.REGION_ID
  ,A.MODALITY_TYPE
  FROM (
    SELECT  /*+   PARALLEL(auto) */
     REF_CUSTOMER
    ,REF_BRANCH
    ,SUM(BALANCE) AS BALANCE
    ,case when max(REF_DEPOSIT_TYPE) in (43,44,540,541,542,543,544,556,557,559,560)then 4 else MAX(MODALITY_TYPE) end AS MODALITY_TYPE
    FROM AKIN.TBL_DEPOSIT
    GROUP BY
     REF_CUSTOMER
    ,REF_BRANCH
   ) A
  ,    TBL_BRANCH B
  WHERE A.REF_BRANCH   = B.BRN_ID;

 END PRC_CONC_REP_PROFILE_REPORT;
/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CONC_STATE_REPORT RETURN VARCHAR2 AS
  VAR_SUM_BALANCE   NUMBER;
 BEGIN
  SELECT
   SUM(BALANCE)
  INTO
   VAR_SUM_BALANCE
  FROM TBL_CONC_RPORT_DETAIL;

  RETURN 'select ref_state "hc-key",sum(balance) "value",max(state_name)"name",to_char(0||round(sum(balance)/' || VAR_SUM_BALANCE || ',5)) as "percent" from tbl_conc_rport_detail group by ref_state order by ref_state'
;
 END FNC_CONC_STATE_REPORT;
/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CONC_MAIN_REPORT RETURN clob AS
  VAR_COUNT         NUMBER;
  VAR_SUM_BALANCE   NUMBER;
  VAR_PERCENT_100   NUMBER;
 BEGIN

   return 'WITH tmp AS
                (SELECT 
                  customer_number,
                  balance,
                  count(balance) over (partition BY 1) total_count,
                  sum(balance) over (partition BY 1) total_balance,
                  ceil(((row_number() over (partition BY 1 order by balance DESC)/COUNT(balance) over (partition BY 1)))*10) deck
                FROM tbl_conc_rport_detail
                )
              SELECT /*+ parallel(auto) */
                deck "1",
                SUM(SUM(balance)) over (partition BY 1 order by deck) "value",
                SUM(count(balance)) over (partition BY 1 order by deck) "count",
                 0||to_char(round(SUM(SUM(balance)) over (partition BY 1 order by deck) / max(total_balance), 5)) "percent"
              FROM tmp
              GROUP BY deck
              ORDER BY deck';

 END FNC_CONC_MAIN_REPORT;

/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CONC_STATE_DETAIL_REPORT ( INPAR_STATE IN NUMBER ) RETURN clob AS
  VAR_COUNT         NUMBER;
  VAR_SUM_BALANCE   NUMBER;
  VAR_PERCENT_100   NUMBER;
 BEGIN
  SELECT /*+ parallel(auto) */
   COUNT(DISTINCT CUSTOMER_NUMBER)
  INTO
   VAR_COUNT
  FROM TBL_CONC_RPORT_DETAIL
  WHERE REF_STATE   = INPAR_STATE;

  SELECT /*+ parallel(auto) */
   SUM(BALANCE)
  INTO
   VAR_SUM_BALANCE
  FROM TBL_CONC_RPORT_DETAIL
  WHERE REF_STATE   = INPAR_STATE;

  SELECT /*+ parallel(auto) */
   TO_CHAR(ROUND(
    SUM(BALANCE) / VAR_SUM_BALANCE
   ,5
   ) * 100)
  INTO
   VAR_PERCENT_100
  FROM (
    SELECT /*+ parallel(auto) */
     CUSTOMER_NUMBER
    ,BALANCE
    FROM (
      SELECT /*+ parallel(auto) */
       CUSTOMER_NUMBER
      ,SUM(BALANCE) AS BALANCE
      FROM TBL_CONC_RPORT_DETAIL
      WHERE REF_STATE   = INPAR_STATE
      GROUP BY
       CUSTOMER_NUMBER
     )
    ORDER BY BALANCE DESC
   )
  WHERE ROWNUM <= 100;


 return 'WITH tmp AS
                (SELECT 
                  customer_number,
                  balance,
                  count(balance) over (partition BY 1) total_count,
                  sum(balance) over (partition BY 1) total_balance,
                  ceil(((row_number() over (partition BY 1 order by balance DESC)/COUNT(balance) over (partition BY 1)))*10) deck
                FROM tbl_conc_rport_detail
                where ref_state = '||  INPAR_STATE  ||'
                )
              SELECT /*+ parallel(auto) */
                deck "1",
                deck*10 "ha",
                SUM(SUM(balance)) over (partition BY 1 order by deck) "value",
                SUM(count(balance)) over (partition BY 1 order by deck) "count",
                (round(SUM(SUM(balance)) over (partition BY 1 order by deck) / max(total_balance), 5)) "percent",
                '|| VAR_PERCENT_100   || ' "top100"
              FROM tmp
              GROUP BY deck
              ORDER BY deck';

 END FNC_CONC_STATE_DETAIL_REPORT;

/*---------------------------------------------------------------------------------------------*/
/***********************************************************************************************/
/*---------------------------------------------------------------------------------------------*/

 FUNCTION FNC_CONC_BRANCH_REPORT RETURN VARCHAR2 AS
  VAR_SUM_BALANCE   NUMBER;
 BEGIN
  SELECT
   SUM(BALANCE)
  INTO
   VAR_SUM_BALANCE
  FROM TBL_CONC_RPORT_DETAIL;

  RETURN 'SELECT
 to_number(REF_BRANCH)"id"
 ,BALANCE "value"
 ,NAME as "name"
 ,to_char(0||round(BALANCE/' || VAR_SUM_BALANCE || ',6)) "percent"
FROM (
  SELECT
   REF_BRANCH
  ,SUM(BALANCE) AS BALANCE
  ,MAX(TBL_BRANCH.NAME) AS NAME
  FROM TBL_CONC_RPORT_DETAIL
  ,  TBL_BRANCH
  WHERE TBL_BRANCH.BRN_ID   = TBL_CONC_RPORT_DETAIL.REF_BRANCH
  GROUP BY
   REF_BRANCH
 )
ORDER BY BALANCE DESC'
;
 END FNC_CONC_BRANCH_REPORT;
