FUNCTION FNC_ACTIVE_PROGRAM ( I_DATE IN DATE ) 
  RETURN VARCHAR2
AS
/*
  @author MYM
  @date 1397-10-10
  @what create a blcok code, for executing today based on tbl_job data
*/
  V_BLOCK VARCHAR2(4000);
  V_CURRENT_DATE DATE;
  V_LENGTH NUMBER;
  CURSOR V_CUR IS 
    SELECT *
    FROM TBL_JOB
    WHERE ENABLE = 1;
BEGIN
  V_BLOCK := 'BEGIN ';
  -- THIS LOOP, ITERAVE ON ALL JOBS TO FIND ACTIVE ONES
  FOR REC IN V_CUR LOOP
    V_CURRENT_DATE := TRUNC(REC.START_DATE);
    V_LENGTH := 1;
    -- THIS LOOP, CHECK THAT IS EXECUTION DATE RECIEVED OR NOT?
    WHILE V_LENGTH <= REC.DURATION_LENGTH AND V_CURRENT_DATE <= I_DATE LOOP
      IF V_CURRENT_DATE = TRUNC(I_DATE) THEN
        V_BLOCK := V_BLOCK || REC.PROGRAM || ';';
        V_LENGTH := 1;
        EXIT;
      ELSE 
        -- DATA ASSINGMENT FOR NEXT LOOP
        V_LENGTH := V_LENGTH+1;
        V_CURRENT_DATE := CASE WHEN REC.DURATION_TYPE LIKE 'WEEKLY' THEN V_CURRENT_DATE + 7
                               WHEN REC.DURATION_TYPE LIKE 'DAILY' THEN V_CURRENT_DATE+1
                               WHEN REC.DURATION_TYPE LIKE 'MONTHLY' THEN ADD_MONTHS(V_CURRENT_DATE, 1)
                               WHEN REC.DURATION_TYPE LIKE 'SEASONAL' THEN ADD_MONTHS(V_CURRENT_DATE, 3)
                               WHEN REC.DURATION_TYPE LIKE 'ANNUAL' THEN ADD_MONTHS(V_CURRENT_DATE, 12)

                          END;
      END IF;
    END LOOP;
  END LOOP;

  V_BLOCK := V_BLOCK || ' END;';

  RETURN V_BLOCK;
END FNC_ACTIVE_PROGRAM;
