FUNCTION FNC_JOB_GET_JOB_DATE(
    INPAR_REPORT IN VARCHAR2 )
  RETURN VARCHAR2
AS
  VAR VARCHAR2(30000);
BEGIN
  VAR := FNC_JOB_STORE_DATE(INPAR_REPORT);
  VAR := 'SELECT 
JOB_DATE "date"
FROM TBL_JOB_STORE_DATE WHERE REF_REPORT  =' || INPAR_REPORT || ' ';
