FUNCTION FNC_JOB_STATUS(
    INPAR_REPORT IN VARCHAR2,
    INPAR_STATUS IN VARCHAR2)
  RETURN VARCHAR2
AS
  VAR VARCHAR2(30000);
  PRAGMA AUTONOMOUS_TRANSACTION;
