function fnc_compare_balance_sheet 
(
  inpar_date in varchar2 
, inpar_cur in number 
, inpar_date2 in varchar2 
, inpar_cur2 in number 
)return varchar2  as
VAR_PARTITION1 VARCHAR2(200) := 'P'||TO_CHAR(to_date(INPAR_DATE,'yyyy-mm-dd'),'J');

VAR_PARTITION2 VARCHAR2(200):= 'P'||TO_CHAR(to_date(INPAR_DATE2,'yyyy-mm-dd'),'J');
  begin
   RETURN 
'SELECT  /*+ PARALLEL(AUTO) */  LEDGER_CODE as "id"
 ,NAME as "text"
 ,DEPTH as "level"
 ,PARENT_CODE as "parent"
  ,nvl("1",0) "x1",nvl("2",0) "x2"
  FROM
(
select  /*+ PARALLEL(AUTO) */  LEDGER_CODE
 ,NAME
 ,DEPTH
 ,PARENT_CODE
 , BALANCE
 ,REF_CUR_ID
  from TBL_LEDGER_ARCHIVE  PARTITION ('||VAR_PARTITION1||') where to_date(EFF_DATE,''yyyy-mm-dd'') =to_date(to_date('''||INPAR_DATE||''',''yyyy-mm-dd''),''yyyy-mm-dd'') and REF_CUR_ID = '||INPAR_cur||'
union
select  /*+ PARALLEL(AUTO) */  LEDGER_CODE
 ,NAME
 ,DEPTH
 ,PARENT_CODE
 ,  BALANCE
 ,REF_CUR_ID+200
 from TBL_LEDGER_ARCHIVE  PARTITION ('||VAR_PARTITION2||') where to_date(EFF_DATE,''yyyy-mm-dd'') =to_date(to_date('''||inpar_date2||''',''yyyy-mm-dd''),''yyyy-mm-dd'') and REF_CUR_ID = '||INPAR_cur2||')
PIVOT
(
  max(balance)
  FOR REF_CUR_ID IN ('||INPAR_cur||' as "1", '||INPAR_cur2||'+200 as "2")
) order by ledger_code';
  end fnc_compare_balance_sheet;
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/
end pkg_balance_sheet;

