  INPAR_DATE_FIRST   VARCHAR2(200);
 INPAR_DATE_LAST    VARCHAR2(200);
 VAR_MAX_REPREQ     NUMBER;
 var_tree varchar2(100);
BEGIN
 SELECT
  FIRST_DATE
 INTO
  INPAR_DATE_FIRST
 FROM TBL_REPORT
 WHERE ID   = INPAR_REPORT;

 SELECT
  last_DATE
 INTO
  INPAR_DATE_LAST
 FROM TBL_REPORT
 WHERE ID   = INPAR_REPORT;

 SELECT
  MAX(ID)
 INTO
  VAR_MAX_REPREQ
