        WHERE
            ref_report = inpar_id
            AND type = 1;

        SELECT
            profile_id
        INTO var_type_profile2
        FROM
            tbl_comparative_stat_pro_det
        WHERE
            ref_report = inpar_id
            AND type = 0;

        SELECT
            MAX(id)
        INTO var_repreq
        FROM
            tbl_repreq
        WHERE
            ref_report_id = inpar_id;
            INSERT INTO tbl_comparative_stat_val (
    ref_report,
    ref_repreq,
    current_month,
    pre_month,
    similar_month_pre_year,
    end_of_pre_year,
    type,
    cur_type,title
) 
