 FUNCTION FNC_com_stat_FINAL_REPORT (

inpar_repreq     IN NUMBER
 ,inpar_CUR_TYPE in number  --1 = riali , 0 = arzi
 ) RETURN VARCHAR2 as 
 var_report number;
 var_currentyear number;
 var_preyear number;
 begin 
 select max(ref_report) into var_report from TBL_COMPARATIVE_STAT_VAL where ref_repreq = inpar_repreq;
  select  substr(TO_CHAR(eff_date,'yyyy/mm/dd','nls_calendar=persian'),1,4)  into  var_currentyear from TBL_COMPARATIVE_STAT_PRO_DET where ref_report = var_report and rownum =1 ;
    select  substr(TO_CHAR(eff_date,'yyyy/mm/dd','nls_calendar=persian'),1,4)-1  into var_preyear from TBL_COMPARATIVE_STAT_PRO_DET where ref_report = var_report and rownum =1 ;
 return 'SELECT 

  val.CURRENT_MONTH as "currentMonth",
  val.SIMILAR_MONTH_PRE_YEAR as "similarMonthPreYear",
  val.END_OF_PRE_YEAR as "esfand",
  val.TYPE as "shobeSetad",
  val.CUR_TYPE as "arziRiyali",
  val.TITLE as "kolOther",
 '||var_currentyear||' as "currentYear" ,
 '||var_preyear||' as "preYear"

FROM TBL_COMPARATIVE_STAT_VAL val
where 

val.ref_repreq =  '||inpar_repreq||'
and val.cur_type =  '||inpar_CUR_TYPE||'';
 end FNC_com_stat_FINAL_REPORT;
