    PROCEDURE PRC_com_stat_PROFILE_REPORT (
        inpar_name               IN VARCHAR2,
        inpar_des                IN VARCHAR2,
        inpar_ref_user           IN VARCHAR2,
        inpar_status             IN VARCHAR2,
        inpar_insert_or_update   IN VARCHAR2,
        inpar_id                 IN VARCHAR2,
        inpar_type               IN VARCHAR2,
        outpar_id                OUT VARCHAR2
    )
        AS
    BEGIN
        IF
            ( inpar_insert_or_update = 0 )
        THEN
            INSERT INTO tbl_report (
                name,
                des,
                create_date,
                ref_user,
                status,
                type,
                category
            ) VALUES (
                inpar_name,
                inpar_des,
                SYSDATE,
                inpar_ref_user,
                inpar_status,
                inpar_type,
                'comStat'
            );

            COMMIT;
            SELECT
                id
            INTO outpar_id
            FROM
                tbl_report
            WHERE
                create_date = (
                    SELECT
                        MAX(create_date)
                    FROM
                        tbl_report
                )
                AND id = (
                    SELECT
                        MAX(id)
                    FROM
                        tbl_report
                );
                update tbl_report set H_id = outpar_id where id  = outpar_id;

        ELSE
            UPDATE tbl_report
            SET
                name = inpar_name,
                des = inpar_des,
                ref_user = inpar_ref_user,
                status = inpar_status,
                type = inpar_type
            WHERE
                id = inpar_id;

            COMMIT;
        END IF;
    END PRC_com_stat_PROFILE_REPORT;
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/

    FUNCTION fnc_com_stat_esfand (
        inpar_date IN DATE
    ) RETURN DATE AS
     var_date   DATE;
     VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(INPAR_DATE,'J');
    BEGIN
        SELECT DISTINCT
            MAX(eff_date)
        INTO var_date
        FROM
            tbl_ledger_archive  PARTITION ( VAR_PARTITION ) 
        WHERE
            substr(TO_CHAR(eff_date,'yyyy/mm/dd','nls_calendar=persian'),1,7) = substr(TO_CHAR(EXTRACT(YEAR FROM TO_DATE(TO_CHAR(inpar_date,'yyyy/mm/dd'
,'nls_calendar=persian'),'yyyy/mm/dd') ) - 1
                                                                                                         || '/12'),1,7);

        RETURN var_date;
    END fnc_com_stat_esfand;
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/

    FUNCTION fnc_com_stat_pre_month (
        inpar_date IN DATE
    ) RETURN DATE AS
        var_date   DATE;
        VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(INPAR_DATE,'J');
    BEGIN
        IF
            ( lpad(substr(TO_CHAR(inpar_date,'yyyy/mm/dd','nls_calendar=persian'),6,2) - 1,2,0) = 01 )
        THEN
            SELECT DISTINCT
                MAX(eff_date)
            INTO var_date
            FROM
                tbl_ledger_archive  PARTITION ( VAR_PARTITION ) 
            WHERE
                substr(TO_CHAR(eff_date,'yyyy/mm/dd','nls_calendar=persian'),1,7) = substr(TO_CHAR(EXTRACT(YEAR FROM TO_DATE(TO_CHAR(inpar_date,'yyyy/mm/dd'
,'nls_calendar=persian'),'yyyy/mm/dd') )
                                                                                                             || '/'
                                                                                                             ||
                    CASE
                        WHEN lpad(substr(TO_CHAR(inpar_date,'yyyy/mm/dd','nls_calendar=persian'),6,2) - 1,2,0) = 01 THEN '12'
                        ELSE lpad(substr(TO_CHAR(inpar_date,'yyyy/mm/dd','nls_calendar=persian'),6,2) - 1,2,0)
                    END
                ),1,7);

        ELSE
            SELECT DISTINCT
                MAX(eff_date)
            INTO var_date
            FROM
                tbl_ledger_archive PARTITION ( VAR_PARTITION ) 
            WHERE
                substr(TO_CHAR(eff_date,'yyyy/mm/dd','nls_calendar=persian'),1,7) = substr(TO_CHAR(EXTRACT(YEAR FROM TO_DATE(TO_CHAR(inpar_date,'yyyy/mm/dd'
,'nls_calendar=persian'),'yyyy/mm/dd') )
                                                                                                             || '/'
                                                                                                             ||
                    CASE
                        WHEN lpad(substr(TO_CHAR(inpar_date,'yyyy/mm/dd','nls_calendar=persian'),6,2) - 1,2,0) = 01 THEN '12'
                        ELSE lpad(substr(TO_CHAR(inpar_date,'yyyy/mm/dd','nls_calendar=persian'),6,2) - 1,2,0)
                    END
                ),1,7);

        END IF;

        RETURN var_date;
    END fnc_com_stat_pre_month;
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/

    FUNCTION fnc_com_stat_pre_year (
        inpar_date IN DATE
    ) RETURN DATE AS
        var_date   DATE;
        VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(INPAR_DATE,'J');
    BEGIN
        SELECT DISTINCT
            MAX(eff_date)
        INTO var_date
        FROM
            tbl_ledger_archive PARTITION ( VAR_PARTITION ) 
        WHERE
            substr(TO_CHAR(eff_date,'yyyy/mm/dd','nls_calendar=persian'),1,7) = substr(TO_CHAR(EXTRACT(YEAR FROM TO_DATE(TO_CHAR(inpar_date,'yyyy/mm/dd'
,'nls_calendar=persian'),'yyyy/mm/dd') ) - 1
                                                                                                         || substr(TO_CHAR(SYSDATE - 1,'yyyy/mm/dd','nls_calendar=persian'),5) ),1,7);

        RETURN var_date;
    END fnc_com_stat_pre_year;
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/

    PROCEDURE prc_com_stat_profile_detail (
        inpar_ref_rep_id         IN VARCHAR2,
        inpar_profile_id         IN VARCHAR2,
        inpar_value              IN VARCHAR2,
        inpar_type               IN VARCHAR2 -- 1 -> mande kole tashilat   0 -> mande motalebate gheire jari
       ,
