FUNCTION fnc_comp_statistic_gi_calc (
    inpar_id         IN NUMBER,
    inpar_date       IN DATE,
    inpar_branch     IN NUMBER,-- 1 = setad 2 = other
    inpar_currency   IN NUMBER-- 1 = rial 2= other
) RETURN VARCHAR2 AS
    var CLOB;
    var2 CLOB;
    var3 CLOB;
    var_branch VARCHAR2(200);
    var_cur VARCHAR2(200);
    VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(INPAR_DATE,'J');
    BEGIN

        if(inpar_branch = 1) then 
        var_branch:=' and ref_branch = 8899 ';
        if(inpar_currency = 1) then 
            var_cur:=' and ref_cur_id = 4 ';
        else
            var_cur:= '   and ref_cur_id <>4  ';
        end if;
    else
        var_branch:=' and ref_branch <> 8899 ';
        if(inpar_currency = 1) then 
            var_cur:=' and ref_cur_id = 4 ';
        else
            var_cur:= ' and ref_cur_id <>4  '; 
        end if; 
    end if;
    SELECT
        '
  SELECT
   REPLACE(
    WMSYS.WM_CONCAT(V)
 ,'',''
 ,''''
   )
   FROM (
    SELECT
     ABS(DG.BALANCE) ||
     A.SPLIT_SING AS V
    FROM (
      WITH T AS (
       SELECT
        REPLACE(FORMULA||''+'','','','''') STR
       FROM TBL_LEDGER_REPORT_MAP
       WHERE id   = '
        || inpar_id
           || '   ) SELECT
       REGEXP_SUBSTR(
        STR
     ,''[0-9]+''
     ,1
     ,LEVEL
       ) SPLIT_VALUES
    ,REGEXP_SUBSTR(
        STR
     ,''[^0-9]+''
     ,1
     ,LEVEL
       ) SPLIT_SING
    ,LEVEL AS LEV
      FROM T
      CONNECT BY
       LEVEL <= (
        SELECT
         LENGTH(REPLACE(STR,''-'',NULL) )
        FROM T
       )
     ) A
  ,TBL_LEDGER_branch  PARTITION ( '|| VAR_PARTITION ||')  DG
    WHERE DG.LEDGER_CODE   = A.SPLIT_VALUES
    '||var_branch||'  '||var_cur||'
     AND
      SPLIT_VALUES IS NOT NULL
        AND
      trunc(DG.EFF_DATE)                                          = trunc(TO_DATE('''
              || inpar_date
                 || ''')))
 '
    INTO var
    FROM
        dual;

    EXECUTE IMMEDIATE var
    INTO var3;
    SELECT
        CASE
            WHEN substr(TO_CHAR(var3),-1) IN (
                '-',
                '+'
            ) THEN var3 || '0'
            ELSE var3
        END
    INTO var
    FROM
        dual;

