function fnc_cr_br_index_esfand (
        inpar_date IN DATE
    ) RETURN DATE AS
        var_date   DATE;
       -- VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(INPAR_DATE,'J');
    BEGIN
        SELECT DISTINCT
            MAX(eff_date)
        INTO var_date
        FROM
            tbl_ledger_archive --PARTITION ( VAR_PARTITION ) 
        WHERE
            substr(TO_CHAR(eff_date,'yyyy/mm/dd','nls_calendar=persian'),1,7) = substr(TO_CHAR(EXTRACT(YEAR FROM TO_DATE(TO_CHAR(inpar_date,'yyyy/mm/dd'
,'nls_calendar=persian'),'yyyy/mm/dd') ) - 1
                                                                                                         || '/12'),1,7);

        RETURN var_date;
  end fnc_cr_br_index_esfand;
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/
   FUNCTION fnc_cr_br_pre_year (
        inpar_date IN DATE
    ) RETURN DATE AS
        var_date   DATE;
              --  VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(INPAR_DATE,'J');

    BEGIN
        SELECT DISTINCT
            MAX(eff_date)
        INTO var_date
        FROM
            tbl_ledger_archive --PARTITION ( VAR_PARTITION ) 
        WHERE
            substr(TO_CHAR(eff_date,'yyyy/mm/dd','nls_calendar=persian'),1,7) = substr(TO_CHAR(EXTRACT(YEAR FROM TO_DATE(TO_CHAR(inpar_date,'yyyy/mm/dd'
,'nls_calendar=persian'),'yyyy/mm/dd') ) - 1
                                                                                                         || substr(TO_CHAR(SYSDATE - 1,'yyyy/mm/dd','nls_calendar=persian'),5) ),1,7);

        RETURN var_date;
    END fnc_cr_br_pre_year;
