procedure prc_cr_br_index_profile_report (
        inpar_name               IN VARCHAR2,
        inpar_des                IN VARCHAR2,
        inpar_ref_user           IN VARCHAR2,
        inpar_status             IN VARCHAR2,
        inpar_insert_or_update   IN VARCHAR2,
        inpar_id                 IN VARCHAR2,
        inpar_type               IN VARCHAR2,
        outpar_id                OUT VARCHAR2
    )
        AS
    BEGIN
        IF
            ( inpar_insert_or_update = 0 )
        THEN
            INSERT INTO tbl_report (
                name,
                des,
                create_date,
                ref_user,
                status,
                type,
                category
            ) VALUES (
                inpar_name,
                inpar_des,
                SYSDATE,
                inpar_ref_user,
                inpar_status,
                'crIndex',
                'crIndex'
            );

            COMMIT;
            SELECT
                id
            INTO outpar_id
            FROM
                tbl_report
            WHERE
                create_date = (
                    SELECT
                        MAX(create_date)
                    FROM
                        tbl_report
                )
                AND id = (
                    SELECT
                        MAX(id)
                    FROM
                        tbl_report
                );
  update tbl_report set H_id = outpar_id where id  = outpar_id;
        ELSE
            UPDATE tbl_report
            SET
                name = inpar_name,
                des = inpar_des,
                ref_user = inpar_ref_user,
                status = inpar_status,
                type = inpar_type
            WHERE
                id = inpar_id;

            COMMIT;
        END IF;
  end prc_cr_br_index_profile_report;
