FUNCTION fnc_creditrisk_br_gi_calc (
    inpar_id         IN NUMBER,
    inpar_date       IN DATE,
    inpar_branch     IN NUMBER,
    inpar_currency   IN NUMBER
) RETURN VARCHAR2 AS
    var CLOB;
    var2 CLOB;
    var3 CLOB;
VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(to_date(INPAR_DATE),'J');
    BEGIN

    SELECT
        '
  SELECT
   REPLACE(
    WMSYS.WM_CONCAT(V)
 ,'',''
 ,''''
   )
   FROM (
    SELECT
     ABS(DG.BALANCE) ||
     A.SPLIT_SING AS V
    FROM (
      WITH T AS (
       SELECT
        REPLACE(FORMULA||''+'','','','''') STR
       FROM TBL_LEDGER_REPORT_MAP
       WHERE id   = '
        || inpar_id
           || '   ) SELECT
       REGEXP_SUBSTR(
        STR
     ,''[0-9]+''
     ,1
     ,LEVEL
       ) SPLIT_VALUES
    ,REGEXP_SUBSTR(
        STR
     ,''[^0-9]+''
     ,1
     ,LEVEL
       ) SPLIT_SING
    ,LEVEL AS LEV
      FROM T
      CONNECT BY
       LEVEL <= (
        SELECT
         LENGTH(REPLACE(STR,''-'',NULL) )
        FROM T
       )
     ) A
  ,TBL_LEDGER_branch PARTITION ( '||VAR_PARTITION||' )  DG
    WHERE DG.LEDGER_CODE   = A.SPLIT_VALUES
     and ref_branch = '||inpar_branch||' and ref_cur_id =  '||inpar_currency||'
     AND
      SPLIT_VALUES IS NOT NULL
        AND
      trunc(DG.EFF_DATE)                                          = trunc(TO_DATE('''
              || inpar_date
                 || ''')))
 '
    INTO var
    FROM
        dual;

    EXECUTE IMMEDIATE var
    INTO var3;
    SELECT
        CASE
            WHEN substr(TO_CHAR(var3),-1) IN (
                '-',
                '+'
            ) THEN var3 || '0'
            ELSE var3
        END
    INTO var
    FROM
        dual;

    EXECUTE IMMEDIATE 'select '
                      || nvl(TO_CHAR(var),0)
                      || ' from dual'
    INTO var2;

    RETURN abs(to_number(var2) );
-- RETURN VAR;
    END fnc_creditrisk_br_gi_calc;



/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/





end pkg_creditrisk_branch_index;

