FUNCTION fnc_shock_gi_calc(
    inpar_id   IN NUMBER,
    inpar_date IN DATE )RETURN VARCHAR2 AS
  VAR    CLOB;
  VAR2   CLOB;
  VAR3   CLOB;
 BEGIN
  SELECT
   '
  SELECT /*+   PARALLEL(auto) */
   REPLACE(
    WMSYS.WM_CONCAT(V)
   ,'',''
   ,''''
   )
   FROM (
    SELECT
     ''(''||DG.BALANCE ||'')''||
     A.SPLIT_SING AS V
    FROM (
      WITH T AS (
       SELECT
        REPLACE(FORMULA||''+'','','','''') STR
       FROM TBL_LEDGER_REPORT_MAP
       WHERE id   = ' ||
   INPAR_ID ||
   '   ) SELECT
       REGEXP_SUBSTR(
        STR
       ,''[0-9]+''
       ,1
       ,LEVEL
       ) SPLIT_VALUES
      ,REGEXP_SUBSTR(
        STR
       ,''[^0-9]+''
       ,1
       ,LEVEL
       ) SPLIT_SING
      ,LEVEL AS LEV
      FROM T
      CONNECT BY
       LEVEL <= (
        SELECT
         LENGTH(REPLACE(STR,''-'',NULL) )
        FROM T
       )
     ) A
    ,TBL_LEDGER_archive DG
    WHERE DG.LEDGER_CODE   = A.SPLIT_VALUES
     AND
      SPLIT_VALUES IS NOT NULL
        AND
      trunc(DG.EFF_DATE)                                          = trunc(TO_DATE(''' ||
   INPAR_DATE ||
   ''')))
 '
  INTO
   VAR
  FROM DUAL;



  EXECUTE IMMEDIATE VAR INTO
   VAR3;
  SELECT
   CASE
    WHEN SUBSTR(
     TO_CHAR(VAR3)
    ,-1
    ) IN (
     '-','+'
    ) THEN VAR3 ||
    '0'
    ELSE VAR3
   END
  INTO
   VAR
  FROM DUAL;

  EXECUTE IMMEDIATE 'select ' ||
  NVL(TO_CHAR(VAR),0) ||
  ' from dual' INTO
   VAR2;

  RETURN (to_number(VAR2));


END fnc_shock_gi_calc;
