PROCEDURE prc_shock_report_value(
    INPAR_ID_REPORT IN VARCHAR2)
AS
VAR_REPREQ number;
VAR_dep number;

VAR_lon number;

VAR_ledger number;

BEGIN
     SELECT
   MAX(ID)
  INTO
   VAR_REPREQ
  FROM TBL_REPREQ
  WHERE REF_REPORT_ID   = INPAR_ID_REPORT;

   select ref_dep_profile,ref_lon_profile,ref_ledger_profiel into VAR_dep,VAR_lon,VAR_ledger  from tbl_report where id = INPAR_ID_REPORT;

for i in ( select  /*+ parallel(auto) */  d,shamsi,s from (
    select  /*+ parallel(auto) */  * from 
    (select  /*+ parallel(auto) */ a.d,A.shamsi,b.s from (
    select  /*+ parallel(auto) */  d, to_char(d,'yyyy/mm/dd','nls_calendar=persian') shamsi, to_char(d,'yyyymm','nls_calendar=persian') as s   from (
    SELECT  /*+ parallel(auto) */ DISTINCT
     FIRST_VALUE(
      to_date(EFF_DATE,'yyyy/mm/dd','nls_calendar=persian')
     ) IGNORE NULLS OVER(PARTITION BY
      substr(eff_date,0,7)
      ORDER BY
       EFF_DATE
      DESC
     ) AS D  
    FROM (
      SELECT  /*+ parallel(auto) */ DISTINCT
       to_char(EFF_DATE,'yyyy/mm/dd','nls_calendar=persian') as EFF_DATE
      FROM TBL_LEDGER_ARCHIVE
     )
    WHERE to_date(EFF_DATE,'yyyy/mm/dd','nls_calendar=persian') > ADD_MONTHS(SYSDATE,-12)
    ORDER BY d)) a right join (Select /*+ parallel(auto) */
    to_char(add_months(trunc(sysdate),-Rownum),'yyyymm','nls_calendar=persian') as s,
     -Rownum
    From dual
    Connect By Rownum <= 12) b
    on b.s = a.s)
     union
    (
    select  /*+ parallel(auto) */  max(eff_date ) d ,to_char(max(eff_date ),'yyyy/mm/dd','nls_calendar=persian'), to_char(trunc(sysdate),'yyyymm','nls_calendar=persian')  s from tbl_ledger_archive  
    ))
    order by s)loop


    INSERT INTO TBL_SHOCK_FINAL_RESULT (

 REF_REPORT
 ,REF_REPREQ
 ,TYPE
 ,MONTH_NAME
 ,VALUE
) VALUES (

 INPAR_ID_REPORT
 ,VAR_REPREQ
 ,1
 ,i.s
 ,pkg_shock.fnc_shock_gi_calc(VAR_dep,i.d)
);
    commit;
        INSERT INTO TBL_SHOCK_FINAL_RESULT (

 REF_REPORT
 ,REF_REPREQ
 ,TYPE
 ,MONTH_NAME
 ,VALUE
) VALUES (

 INPAR_ID_REPORT
 ,VAR_REPREQ
 ,2
 ,i.s
 ,pkg_shock.fnc_shock_gi_calc(VAR_lon,i.d)
);
commit;
    INSERT INTO TBL_SHOCK_FINAL_RESULT (

 REF_REPORT
 ,REF_REPREQ
 ,TYPE
 ,MONTH_NAME
 ,VALUE
) VALUES (

 INPAR_ID_REPORT
 ,VAR_REPREQ
 ,3
 ,i.s
 ,pkg_shock.fnc_shock_gi_calc(VAR_ledger,i.d)
);
commit;
    end loop;


 END prc_shock_report_value;
