PROCEDURE prc_shock_profile_report(
    inpar_name             IN VARCHAR2 ,
    inpar_des              IN VARCHAR2 ,
    inpar_ref_user         IN VARCHAR2 ,
    inpar_status           IN VARCHAR2 ,
    inpar_insert_or_update IN VARCHAR2 ,
    inpar_id               IN VARCHAR2 ,
    inpar_dep_id           IN VARCHAR2 ,
    inpar_darayi           IN VARCHAR2 ,
    inpar_darayi_naghd     IN VARCHAR2 ,
    outpar_id OUT VARCHAR2 )
AS
BEGIN
   IF
            ( inpar_insert_or_update = 0 )
        THEN
            INSERT INTO tbl_report (
                name,
                des,
                create_date,
                ref_user,
                status,
                type,
                category,
                ref_dep_profile,
                ref_LON_profile,
                ref_ledger_profiel
            ) VALUES (
                inpar_name,
                inpar_des,
                SYSDATE,
                inpar_ref_user,
                inpar_status,
                'shock',
                'shock',
                inpar_dep_id,
                inpar_darayi
                ,inpar_darayi_naghd

            );

            COMMIT;
            SELECT
                id
            INTO outpar_id
            FROM
                tbl_report
            WHERE
                create_date = (
                    SELECT
                        MAX(create_date)
                    FROM
                        tbl_report
                )
                AND id = (
                    SELECT
                        MAX(id)
                    FROM
                        tbl_report
                );
  update tbl_report set H_id = outpar_id where id  = outpar_id;
        ELSE
            UPDATE tbl_report
            SET
                name = inpar_name,
                des = inpar_des,
                ref_user = inpar_ref_user,
                status = inpar_status,
                ref_dep_profile = inpar_dep_id,
                ref_LON_profile = inpar_darayi,
                ref_ledger_profiel = inpar_darayi_naghd
            WHERE
                id = inpar_id;

            COMMIT;
        END IF;

END prc_shock_profile_report;
