 ,INPAR_INSERT_OR_UPDATE   IN VARCHAR2
 ,INPAR_ID                 IN VARCHAR2
 ,INPAR_TYPE               IN VARCHAR2
 ,OUTPAR_ID                OUT VARCHAR2
 )
  AS
 BEGIN
  IF
   ( INPAR_INSERT_OR_UPDATE = 0 )
  THEN
   INSERT INTO TBL_REPORT (
    NAME
   ,DES
   ,CREATE_DATE
   ,REF_USER
   ,STATUS
   ,CATEGORY
   ,TYPE
   ) VALUES (
    INPAR_NAME
   ,INPAR_DES
   ,SYSDATE
   ,INPAR_REF_USER
   ,INPAR_STATUS
   ,'sensAnalysis'
   ,INPAR_TYPE
   );

   COMMIT;
   SELECT
    ID
   INTO
    OUTPAR_ID
   FROM TBL_REPORT
   WHERE CREATE_DATE   = (
      SELECT
       MAX(CREATE_DATE)
      FROM TBL_REPORT
     )
    AND
     ID            = (
      SELECT
       MAX(ID)
      FROM TBL_REPORT
     );

  ELSE
   UPDATE TBL_REPORT
    SET
     NAME = INPAR_NAME
    ,DES = INPAR_DES
    ,REF_USER = INPAR_REF_USER
    ,STATUS = INPAR_STATUS
    ,TYPE = INPAR_TYPE
   WHERE ID   = INPAR_ID;

   COMMIT;
  END IF;

  UPDATE TBL_REPORT
   SET
    H_ID = ID
  WHERE TYPE   = 'sensAnalysis'
   AND
    H_ID IS NULL;

  COMMIT;
 END PRC_SENS_ANALYSIS_REPORT;
