 FUNCTION FNC_SENS_ANALYSIS_GI_CALC (
  INPAR_ID     IN VARCHAR2
 ,INPAR_DATE   IN VARCHAR2
 ) RETURN VARCHAR2 AS
  VAR    CLOB;
  VAR2   CLOB;
  VAR3   CLOB;
  VAR_PARTITION VARCHAR2(200) := 'P'||TO_CHAR(INPAR_DATE,'J');
 BEGIN
  IF
   ( INPAR_ID IS NOT NULL
   )
  THEN
   SELECT /*+  PARALLEL(auto) */
    '  
SELECT  /*+  PARALLEL(auto) */  
REPLACE(    
WMSYS.WM_CONCAT(V)   
,'',''   
,''''   
)   
FROM (    
SELECT  /*+  PARALLEL(auto) */   
ABS(DG.BALANCE) ||     
A.SPLIT_SING AS V    
FROM (      
WITH T AS (       
SELECT      
REPLACE(FORMULA||''+'','','','''') STR       
FROM TBL_LEDGER_REPORT_MAP       
WHERE id   = ' ||
    INPAR_ID ||
    '   ) SELECT       
REGEXP_SUBSTR(        
STR       
,''[0-9]+''       
,1       
,LEVEL       
) SPLIT_VALUES      
,REGEXP_SUBSTR(        
STR       
,''[^0-9]+''       
,1       
,LEVEL       
) SPLIT_SING      
,LEVEL AS LEV      
FROM T      
CONNECT BY       
LEVEL <= (        
SELECT         
LENGTH(REPLACE(STR,''-'',NULL) )        
FROM T       
)     
) A    
,TBL_LEDGER_archive PARTITION ('||VAR_PARTITION||')  DG    
WHERE DG.LEDGER_CODE   = A.SPLIT_VALUES     
AND      
SPLIT_VALUES IS NOT NULL        
AND      
trunc(DG.EFF_DATE)                                          = trunc(TO_DATE(''' ||
    INPAR_DATE ||
    ''',''yyyy-mm-dd'')))
 '
   INTO
    VAR
   FROM DUAL;

   EXECUTE IMMEDIATE VAR INTO
    VAR3;
   SELECT
    CASE
     WHEN SUBSTR(
      TO_CHAR(VAR3)
     ,-1
     ) IN (
      '-','+'
     ) THEN VAR3 ||
     '0'
     ELSE VAR3
    END
   INTO
    VAR
   FROM DUAL;

   EXECUTE IMMEDIATE 'select ' ||
   NVL(TO_CHAR(VAR),0) ||
   ' from dual' INTO
    VAR2;

   RETURN ABS(TO_NUMBER(VAR2) );
  ELSE
   RETURN 0;
  END IF;
 END FNC_SENS_ANALYSIS_GI_CALC;
