 ,INPAR_PROFILE_ID         IN VARCHAR2
 ,INPAR_VALUE              IN VARCHAR2
 ,INPAR_IS_STANDARD        IN VARCHAR2
 ,INPAR_TYPE               IN VARCHAR2
 ,INPAR_INSERT_OR_UPDATE   IN VARCHAR2
 ,INPAR_ID                 IN VARCHAR2
 ,INPAR_TITLE              IN VARCHAR2
 ,OUTPAR_ID                OUT VARCHAR2
 )
  AS
  var_repreq number;
 BEGIN
  IF
   ( INPAR_INSERT_OR_UPDATE = 0 )
  THEN
   INSERT INTO TBL_SENS_ANALYSIS_PRO_DET (
    REF_REPORT
   ,NAME
   ,PROFILE_ID
   ,VALUE
   ,IS_STANDARD
   ,TYPE
   ,TITLE
   ) VALUES (
    INPAR_REF_REPORT
   ,INPAR_NAME
   ,INPAR_PROFILE_ID
   ,INPAR_VALUE
   ,0
   ,INPAR_TYPE
   ,INPAR_TITLE
   );

   COMMIT;
    /*   SELECT*/
    /*    ID*/
    /*   INTO*/
    /*    OUTPAR_ID*/
    /*   FROM TBL_COM_REP_PROFILE_DETAIL*/
    /*   WHERE REF_REPORT   = INPAR_REF_REPORT;*/
   OUTPAR_ID   := INPAR_REF_REPORT;
  ELSE
   UPDATE TBL_SENS_ANALYSIS_PRO_DET
    SET
     REF_REPORT = INPAR_REF_REPORT
    ,NAME = INPAR_NAME
    ,PROFILE_ID = INPAR_PROFILE_ID
    ,VALUE = INPAR_VALUE
    ,IS_STANDARD = 0
    ,TYPE = INPAR_TYPE
   WHERE ID   = INPAR_ID
   and IS_STANDARD <>1;
commit;

select max(ref_repreq) into var_repreq from TBL_NOTIFICATIONS where ref_report = INPAR_REF_REPORT; 
delete from tbl_notifications
where ref_repreq = var_repreq;
commit;

  END IF;

  COMMIT;
 END PRC_SENS_ANALYSIS_DETAIL;
