 PROCEDURE PRC_SENS_UPDATE_GI_CALC ( INPAR_REF_REPORT IN VARCHAR2,INPAR_DATE VARCHAR2 )
  AS
 BEGIN


  FOR I IN (
   SELECT
    *
   FROM TBL_SENS_ANALYSIS_PRO_DET
   WHERE REF_REPORT   = INPAR_REF_REPORT
  ) LOOP
   IF
    ( I.PROFILE_ID IS NOT NULL
