   THEN
    UPDATE TBL_SENS_ANALYSIS_PRO_DET
     SET
      VALUE = PKG_SENSIVITY_ANALYSIS_TEST.FNC_SENS_ANALYSIS_GI_CALC(I.PROFILE_ID,INPAR_DATE)
    WHERE REF_REPORT   = INPAR_REF_REPORT
     AND
      I.ID         = ID;

    COMMIT;
   END IF;
  END LOOP;





 END PRC_SENS_UPDATE_GI_CALC;
