 FUNCTION FNC_SENS_GET_FINAL_RESULT (
  INPAR_REPREQ   IN NUMBER
 ,INPAR_TYPE     IN NUMBER
 ) RETURN VARCHAR2
  AS
 BEGIN
  IF
   ( INPAR_TYPE = 1 )
  THEN

   RETURN 'SELECT
 case when ("ssby"+"ssky"+ "g5" + "tdstm" + "spb" = 0 ) then 0 else  round(("kht"/(0.88*("ssby")+ 0.6*(0.88*("ssky"+"g5"))+"tdstm" + "spb"))*100 ,3) end as "100",
  case when ("ssby"+"ssky"+ "g5" + "tdstm" + "spb" = 0 ) then 0 else round(("kht"/(0.88*("ssby")+ 0.95*(0.6*(0.88*("ssky"+"g5")))+"tdstm" + "spb" ))*100,3) end as "5",
  case when ("ssby"+"ssky"+ "g5" + "tdstm" + "spb" = 0 ) then 0 else round(("kht"/(0.88*("ssby")+ 0.90*(0.6*(0.88*("ssky"+"g5")))+"tdstm" + "spb"))*100 ,3) end as "10",
  case when ("ssby"+"ssky"+ "g5" + "tdstm" + "spb" = 0 ) then 0 else round(("kht"/(0.88*("ssby")+ 0.85*(0.6*(0.88*("ssky"+"g5")))+"tdstm" + "spb"))*100 ,3) end as "15",
  case when ("ssby"+"ssky"+ "g5" + "tdstm" + "spb" = 0 ) then 0 else round(("kht"/(0.88*("ssby")+ 0.80*(0.6*(0.88*("ssky"+"g5")))+"tdstm" + "spb"))*100,3) end as "20",
 case when ("ssby"+"ssky"+ "g5" + "tdstm" + "spb" = 0 ) then 0 else round(("kht"/(0.88*("ssby")+ 0.70*(0.6*(0.88*("ssky"+"g5")))+"tdstm" + "spb"))*100,3) end as "30"
FROM (
  SELECT
   TITLE
  ,TBL_SENS_ANALYSIS_PRO_DET.VALUE
  FROM TBL_SENS_ANALYSIS_PRO_DET
  WHERE TBL_SENS_ANALYSIS_PRO_DET.REF_REPREQ   ='
|| INPAR_REPREQ || '
 )
  PIVOT ( MAX ( VALUE )
   FOR TITLE
   IN ( ''kht'' AS "kht",''ssby'' AS "ssby",''ssky'' AS "ssky",''tdstm'' AS "tdstm",''s'' AS "s",''mnbm'' AS "mnbm",''mnsb'' AS "mnsb",''bb'' AS "bb",''aty''
AS "aty",''assm'' AS "assm",''assk'' AS "assk",''g5'' AS "g5",''bbb'' AS "bbb",''snb'' AS "snb",''zb'' AS "zb",''za'' AS "za",''spb'' AS "spb" )
  )'
;
  ELSIF ( INPAR_TYPE = 2 ) THEN
   RETURN 'SELECT
  case when ("ks" =  0 ) then 0 else round(("kht"/(0.88*("ks")))*100,3)  end as "100",
 case when ("ks" =  0 ) then 0 else round(("kht"/0.95*(0.88*("ks")))*100,3) end as   "5",
  case when ("ks" =  0 ) then 0 else  round(("kht"/0.90*(0.88*("ks")))*100,3) end as "10",
  case when ("ks" =  0 ) then 0 else  round(("kht"/0.85*(0.88*("ks")))*100,3) end as "15",
  case when ("ks" =  0 ) then 0 else round(("kht"/0.80*(0.88*("ks")))*100,3) end as "20",
  case when ("ks" =  0 ) then 0 else round(("kht"/0.70*(0.88*("ks")))*100,3)  end as "30"
FROM (
  SELECT
   TITLE
  ,TBL_SENS_ANALYSIS_PRO_DET.VALUE
  FROM TBL_SENS_ANALYSIS_PRO_DET
  WHERE TBL_SENS_ANALYSIS_PRO_DET.REF_REPREQ   ='
|| INPAR_REPREQ || '
 )
  PIVOT ( MAX ( VALUE )
   FOR TITLE
   IN ( ''kht'' AS "kht",''ks'' AS "ks",''tdstm'' AS "tdstm",''s'' AS "s",''mnbm'' AS "mnbm",''mnsb'' AS "mnsb",''bb'' AS "bb",''aty''
AS "aty",''assm'' AS "assm",''assk'' AS "assk",''g5'' AS "g5",''bbb'' AS "bbb",''snb'' AS "snb",''zb'' AS "zb",''za'' AS "za",''spb'' AS "spb" )
  )'
;
  ELSIF ( INPAR_TYPE = 3 ) THEN
   RETURN 'SELECT
 case when ("bbb" + "bb" =  0 ) then 0 else  ROUND(
  ( ("s" + "mnbm" + "mnsb" + "snb") / ("bbb" + "bb") ) * 100
 ,3
 )  end AS "100"
 ,case when ("bbb" + "bb" =  0 ) then 0 else   ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (0.1 * "bbb") ) / ("bb" + 0.9 * "bbb") ) * 100
 ,3
 ) end AS "f10"


 ,case when ("bbb" + "bb" =  0 ) then 0 else   ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (0.15 * "bbb") ) / ("bb" + 0.85 * "bbb") ) * 100
 ,3
 ) end AS "f15"


 ,case when ("bbb" + "bb" =  0 ) then 0 else  ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (0.2 * "bbb") ) / ("bb" + 0.8 * "bbb") ) * 100
 ,3
 ) end AS "f20"
 ,case when ("bbb" + "bb" =  0 ) then 0 else  ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (0.25 * "bbb") ) / ("bb" + 0.75 * "bbb") ) * 100
 ,3
 ) end  AS "f25"
 ,case when ("bbb" + "bb" =  0 ) then 0 else ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (0.5 * "bbb") ) / ("bb" + 0.5 * "bbb") ) * 100
 ,3
 ) end AS "f50"
 ,case when ("bbb" + "bb" =  0 ) then 0 else ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (0.75 * "bbb") ) / ("bb" + 0.25 * "bbb") ) * 100
 ,3
 ) end  AS "f75"
 ,case when ("bbb" + "bb" =  0 ) then 0 else ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (1 * "bbb") ) / ("bb" + 0 * "bbb") ) * 100
 ,3
 ) end  AS "f100"
 ,case when ("bbb" + "bb" =  0 ) then 0 else  ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (0.88 * 0.1 * "bb") ) / ("bbb" + 0.9 * "bb") ) * 100
 ,3
 ) end AS "s10"



 ,case when ("bbb" + "bb" =  0 ) then 0 else ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (0.88 * 0.15* "bb") ) / ("bbb" + 0.85 * "bb") ) * 100
 ,3
 ) end AS "s15"



 ,case when ("bbb" + "bb" =  0 ) then 0 else ROUND(
  ( ( ("s" + "mnbm" + "mnsb" + "snb") - (0.88 * 0.2 * "bb") ) / ("bbb" + 0.8 * "bb") ) * 100
 ,3
 ) end AS "s20"
FROM (
  SELECT
   TITLE
  ,TBL_SENS_ANALYSIS_PRO_DET.VALUE
  FROM TBL_SENS_ANALYSIS_PRO_DET
  WHERE TBL_SENS_ANALYSIS_PRO_DET.REF_REPREQ   ='
|| INPAR_REPREQ || '
 )
  PIVOT ( MAX ( VALUE )
   FOR TITLE
   IN ( ''kht'' AS "kht",''ssby'' AS "ssby",''ssky'' AS "ssky",''tdstm'' AS "tdstm",''s'' AS "s",''mnbm'' AS "mnbm",''mnsb'' AS "mnsb",''bb'' AS "bb",''aty''
AS "aty",''assm'' AS "assm",''assk'' AS "assk",''g5'' AS "g5",''bbb'' AS "bbb",''snb'' AS "snb",''zb'' AS "zb",''za'' AS "za",''spb'' AS "spb" )
  )'
;
  END IF;

  RETURN NULL;
 END FNC_SENS_GET_FINAL_RESULT;
/*---------------------------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------------------------*/

END PKG_SENSIVITY_ANALYSIS_TEST;

  CREATE OR REPLACE PACKAGE "PRAGG"."PKG_LOG" AS 

  /* TODO enter package declarations (types, exceptions, methods etc) here */ 
  PROCEDURE PRC_CALL(I_BLOCKNAME VARCHAR2, I_PARAMETERS VARCHAR2, I_USER VARCHAR2);

  FUNCTION FNC_GET_LOG_UI RETURN VARCHAR2;



   FUNCTION FNC_GET_LOG_inout RETURN VARCHAR2;

