CREATE OR REPLACE PACKAGE BODY "PRAGG"."PKG_LOG" AS
----------------------------------------------------------------------------------------------
----------------------------------------------------------------------------------------------
  PROCEDURE PRC_CALL( I_BLOCKNAME VARCHAR2, I_PARAMETERS VARCHAR2, I_USER VARCHAR2) AS
  /*
    @author MYM
    @date 1397-11-19
    @what logs proc/func informations that called from ui
  */
  BEGIN
    IF I_BLOCKNAME <> 'PRC_NOTIFICATION' THEN
      INSERT INTO TBL_LOG_UI(BLOCKNAME, PARAMETERS, CALLDATE, CALLTIME, USERNAME)
      VALUES(I_BLOCKNAME, I_PARAMETERS, TRUNC(SYSDATE), TO_CHAR(SYSTIMESTAMP, 'HH24:MI:SS:FF3'), I_USER);
    END IF;
  END PRC_CALL;
----------------------------------------------------------------------------------------------
----------------------------------------------------------------------------------------------
  FUNCTION FNC_GET_LOG_UI RETURN VARCHAR2 AS 
  /*
    @author MYM
    @date 1397-11-16
    @what get query of show log table in ui side
  */
  BEGIN
    RETURN 'SELECT  /*+  PARALLEL(auto) */ TT.PERSIANEQUIVALENT "action", TO_CHAR(T.CALLDATE, ''yyyy/mm/dd'', ''nls_calendar=persian'') "date", T.CALLTIME "time", T.USERNAME "user"
            FROM TBL_LOG_UI T, TBL_LOG_UI_MAP TT
            WHERE T.BLOCKNAME = TT.BLOCKNAME and TT.PERSIANEQUIVALENT is not null 
            ORDER BY T.CALLDATE DESC,T.CALLTIME DESC';
  END FNC_GET_LOG_UI;
----------------------------------------------------------------------------------------------
----------------------------------------------------------------------------------------------
FUNCTION FNC_GET_LOG_inout RETURN VARCHAR2 as
/*
    @author NAVID
    @date 1397-11-16
    @what log for login and logout
  */
begin

return 'SELECT 

  TO_CHAR(ual.insert_date, ''yyyy/mm/dd'', ''nls_calendar=persian'') as "date" ,
  to_char( ual.INSERT_DATE ,''hh.mi.ss'') as  "time",
  ual.SUCCEED as "isSucceed",
  uo.USERNAME as "user",
  case when  ual.type = 0 then '''' else '''' end as "action"
FROM SAMAD_NEW.USERS_AUTH_LOGS ual , SAMAD_NEW.USERS_ORGS uo
where ual.USER_ORG_ID = uo.ID
order by 1 desc , 2 desc';

end ;
----------------------------------------------------------------------------------------------
----------------------------------------------------------------------------------------------
END PKG_LOG;

