  PROCEDURE PRC_REPORT_CREATE(I_JSON clob, O_FAKE OUT VARCHAR2) AS
    V_INSERT_UPDATE VARCHAR2(20);
    V_JSON JSON;
    V_TYPE VARCHAR2(50);
    V_CATEGORY VARCHAR2(50);
    V_FAKE number;
    V_ERR_CODE VARCHAR2(50);
    V_ERR_MSG VARCHAR2(1000);
    V_OTHER VARCHAR2(500);

BEGIN
  -- CHECK IF REPORT NAME IS EXIST
  V_JSON := JSON(I_JSON);  
  V_TYPE := json_ext.get_string(v_json, 'type');
  V_CATEGORY := json_ext.get_string(v_json, 'category');
  V_OTHER := json_ext.get_string(v_json, 'other');

  IF (UPPER(json_ext.get_string(v_json, 'insertOrUpdate')) = 'INSERT') THEN
    V_INSERT_UPDATE := 0;
  ELSE 
    V_INSERT_UPDATE := 1;
  END IF;

  IF V_OTHER IS NULL THEN
      IF (V_TYPE = 'aes')THEN 
        DYNAMIC_LQ.PKG_AES.PRC_REP_PROFILE_REPORT(json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), json_ext.get_string(v_json, 'user') , json_ext.get_string(v_json, 'profileDep') ,json_ext.get_string(v_json, 'profileLon') ,json_ext.get_string(v_json, 'profileBrn'), json_ext.get_string(v_json, 'type') , json_ext.get_string(v_json, 'status'), V_INSERT_UPDATE , json_ext.get_string(v_json, 'id'), o_fake);  
      ELSIF (V_TYPE = 'idps')THEN 
        PRAGG.PKG_IDPS.PRC_IDPS_REP_PROFILE_REPORT(json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), json_ext.get_string(v_json, 'branch'), json_ext.get_string(v_json, 'startDate'), json_ext.get_string(v_json, 'endDate'), json_ext.get_string(v_json, 'duration'),  v_insert_update, json_ext.get_string(v_json, 'id'), json_ext.get_string(v_json, 'type'), o_fake);
      ELSIF (V_TYPE = 'duedate_loan') then
        PRAGG.PKG_DUE_DATE_LOAN.PRC_DUE_DATE_PROFILE_REPORT(json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), json_ext.get_string(v_json, 'timingProfile'), v_insert_update, json_ext.get_string(v_json, 'id'), v_type, json_ext.get_string(v_json, 'dateType'), o_fake);
      ELSIF (V_TYPE = 'duedate') then
        PRAGG.PKG_DUE_DATE.PRC_DUE_DATE_PROFILE_REPORT(json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), json_ext.get_string(v_json, 'timingProfile'), v_insert_update, json_ext.get_string(v_json, 'id'), v_type, json_ext.get_string(v_json, 'dateType'), o_fake);
      ELSIF ((V_CATEGORY = 'gap' or V_CATEGORY = 'liquidity') AND V_TYPE = 'rial') THEN -- INSERT OR UPDATE: DUAL STANDARD BY SOBHAN AND SOHA
        PRAGG.PRC_REPORT_PROFILE( -1*V_INSERT_UPDATE+1, json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), sysdate, json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), json_ext.get_string(v_json, 'ledgerProfile'), json_ext.get_string(v_json, 'timingProfile'),json_ext.get_string(v_json, 'dateType'), json_ext.get_string(v_json, 'depositProfile'), json_ext.get_string(v_json, 'profileLon') ,json_ext.get_string(v_json, 'profileBrn'), json_ext.get_string(v_json, 'customerProfile'), json_ext.get_string(v_json, 'currencyProfile'), v_type, v_category, json_ext.get_string(v_json, 'liken'), o_fake);
      ELSIF ((V_CATEGORY = 'gap' or V_CATEGORY = 'liquidity') AND V_TYPE = 'currency') THEN -- INSERT OR UPDATE: DUAL STANDARD BY SOBHAN AND SOHA
        PRAGG.PRC_REPORT_PROFILE( -1*V_INSERT_UPDATE+1, json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), sysdate, json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), json_ext.get_string(v_json, 'ledgerProfile'), json_ext.get_string(v_json, 'timingProfile'),json_ext.get_string(v_json, 'dateType'), json_ext.get_string(v_json, 'depositProfile'), json_ext.get_string(v_json, 'profileLon') ,json_ext.get_string(v_json, 'profileBrn'), json_ext.get_string(v_json, 'customerProfile'), json_ext.get_string(v_json, 'currencyProfile'), v_type, v_category, json_ext.get_string(v_json, 'liken'), o_fake);
      ELSIF ((V_CATEGORY = 'gap' or V_CATEGORY = 'liquidity') AND V_TYPE = 'composite') THEN -- INSERT OR UPDATE: DUAL STANDARD BY SOBHAN AND SOHA
        PRAGG.PRC_REPORT_PROFILE( -1*V_INSERT_UPDATE+1, json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), sysdate, json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), json_ext.get_string(v_json, 'ledgerProfile'), json_ext.get_string(v_json, 'timingProfile'),json_ext.get_string(v_json, 'dateType'), json_ext.get_string(v_json, 'depositProfile'), json_ext.get_string(v_json, 'profileLon') ,json_ext.get_string(v_json, 'profileBrn'), json_ext.get_string(v_json, 'customerProfile'), json_ext.get_string(v_json, 'currencyProfile'), v_type, v_category, json_ext.get_string(v_json, 'liken'), o_fake);
      ELSIF ((V_CATEGORY = 'gap' or V_CATEGORY = 'liquidity') AND V_TYPE = 'rialBranch') THEN -- INSERT OR UPDATE: DUAL STANDARD BY SOBHAN AND SOHA
        PRAGG.PRC_REPORT_PROFILE( -1*V_INSERT_UPDATE+1, json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), sysdate, json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), json_ext.get_string(v_json, 'ledgerProfile'), json_ext.get_string(v_json, 'timingProfile'),json_ext.get_string(v_json, 'dateType'), json_ext.get_string(v_json, 'depositProfile'), json_ext.get_string(v_json, 'profileLon') ,json_ext.get_string(v_json, 'profileBrn'), json_ext.get_string(v_json, 'customerProfile'), json_ext.get_string(v_json, 'currencyProfile'), v_type, v_category, json_ext.get_string(v_json, 'liken'), o_fake);
      ELSIF ((V_CATEGORY = 'gap' or V_CATEGORY = 'liquidity') AND V_TYPE = 'currencyBranch') THEN -- INSERT OR UPDATE: DUAL STANDARD BY SOBHAN AND SOHA
        PRAGG.PRC_REPORT_PROFILE( -1*V_INSERT_UPDATE+1, json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), sysdate, json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), json_ext.get_string(v_json, 'ledgerProfile'), json_ext.get_string(v_json, 'timingProfile'),json_ext.get_string(v_json, 'dateType'), json_ext.get_string(v_json, 'depositProfile'), json_ext.get_string(v_json, 'profileLon') ,json_ext.get_string(v_json, 'profileBrn'), json_ext.get_string(v_json, 'customerProfile'), json_ext.get_string(v_json, 'currencyProfile'), v_type, v_category, json_ext.get_string(v_json, 'liken'), o_fake);
      ELSIF ((V_CATEGORY = 'gap' or V_CATEGORY = 'liquidity') AND V_TYPE = 'compositeBranch') THEN -- INSERT OR UPDATE: DUAL STANDARD BY SOBHAN AND SOHA
        PRAGG.PRC_REPORT_PROFILE( -1*V_INSERT_UPDATE+1, json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), sysdate, json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), json_ext.get_string(v_json, 'ledgerProfile'), json_ext.get_string(v_json, 'timingProfile'),json_ext.get_string(v_json, 'dateType'), json_ext.get_string(v_json, 'depositProfile'), json_ext.get_string(v_json, 'profileLon') ,json_ext.get_string(v_json, 'profileBrn'), json_ext.get_string(v_json, 'customerProfile'), json_ext.get_string(v_json, 'currencyProfile'), v_type, v_category, json_ext.get_string(v_json, 'liken'), o_fake);
      ELSIF (UPPER(V_CATEGORY) = UPPER('dddr') AND UPPER(V_TYPE) = UPPER('dddr')) THEN
        PKG_DUE_DATE_DEPOSIT_RATE.PRC_DDDR_REP_PROFILE_REPORT(json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), V_INSERT_UPDATE, json_ext.get_string(v_json, 'id'), v_type, json_ext.get_string(v_json, 'firstDate'), json_ext.get_string(v_json, 'lastDate'), json_ext.get_string(v_json, 'tree'), o_fake);
      ELSIF (UPPER(V_CATEGORY) = UPPER('ddlt') AND UPPER(V_TYPE) = UPPER('ddlt')) THEN
        PKG_DUE_DATE_LOAN_TYPE.PRC_ddlt_REP_PROFILE_REPORT(json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'), V_INSERT_UPDATE, json_ext.get_string(v_json, 'id'), v_type, json_ext.get_string(v_json, 'firstDate'), json_ext.get_string(v_json, 'lastDate'), json_ext.get_string(v_json, 'tree'), o_fake);
      ELSIF (UPPER(V_CATEGORY) = UPPER('duration2') AND UPPER(V_TYPE) = UPPER('duration2')) THEN --diresh
        PKG_DU_GAP_PROF.PRC_DU_GAP_PROF_PROFILE_REPORT(json_ext.get_string(v_json, 'name'), json_ext.get_string(v_json, 'des'), json_ext.get_string(v_json, 'user'), json_ext.get_string(v_json, 'status'),  json_ext.get_string(v_json, 'depositProfile'), json_ext.get_string(v_json, 'profileLon'), json_ext.get_string(v_json, 'customerProfile'), V_INSERT_UPDATE, json_ext.get_string(v_json, 'id'), v_type, json_ext.get_string(v_json, 'firstDate'), json_ext.get_string(v_json, 'changeRate'),  json_ext.get_string(v_json, 'tahlilHassasiat'),   json_ext.get_string(v_json, 'tasirHassasiat'), o_fake);
      ELSIF (UPPER(V_CATEGORY) = UPPER('GAP_NIIM') AND UPPER(V_TYPE) = UPPER('GAP_NIIM')) THEN --gapNiim
        PKG_GAP_NIIM.PRC_GAP_NIIM_PROFILE_REPORT(json_ext.get_string(v_json, 'name'),json_ext.get_string(v_json, 'des'),json_ext.get_string(v_json, 'user'),json_ext.get_string(v_json, 'status'),v_insert_update,json_ext.get_string(v_json, 'id'),v_type,json_ext.get_string(v_json, 'timingProfile'),json_ext.get_string(v_json, 'depositProfile'),json_ext.get_string(v_json, 'profileLon'),json_ext.get_string(v_json, 'customerProfile'),json_ext.get_string(v_json, 'currencyProfile'),json_ext.get_string(v_json, 'profileBrn'),json_ext.get_string(v_json, 'dateType'),json_ext.get_string(v_json, 'tahlilHassassiat'),json_ext.get_string(v_json, 'tasirHassassiat'),o_fake);
      END IF;
  ELSE
    AKIN.PRC_SHIVE_NEGASHT(json_ext.get_string(v_json, 'ledgerCode'), json_ext.get_string(v_json, 'onem'), json_ext.get_string(v_json, 'from1to3m'), json_ext.get_string(v_json, 'from3to12m'), json_ext.get_string(v_json, 'from1to5y'), json_ext.get_string(v_json, 'g5y'), json_ext.get_string(v_json, 'outOfStream'), O_FAKE);
  END IF;
  COMMIT;



  -- EXCEPTION HANDLING AREA
  EXCEPTION 
    WHEN OTHERS THEN
      V_err_code := SQLCODE;
      V_err_msg := SQLERRM;--err_msg := SUBSTR(SQLERRM, 1, 200);
      INSERT INTO TBL_LOG_ERROR(CODE, MESSAGE, RELEASE_POINT)
      VALUES(V_err_code, V_err_msg, $$plsql_unit);
      --RAISE I_AM_SORRY;

END PRC_REPORT_CREATE;
