  PROCEDURE PRC_REPORT_DELETE(
   I_JSON IN VARCHAR2
  ,O_FAKE OUT NUMBER
  ) AS
   V_TYPE VARCHAR2(50);
   V_JSON JSON;
  BEGIN
    V_JSON := JSON(I_JSON);  
    V_TYPE := json_ext.get_string(v_json, 'type');

    DELETE FROM TBL_REPORT WHERE ID = json_ext.get_string(v_json, 'repReq');

    IF V_TYPE = 'idps' THEN
      DELETE FROM TBL_IDPS_REP_PROFILE_DETAIL WHERE REF_REPORT = json_ext.get_string(v_json, 'repReq');
    END IF;

  END PRC_REPORT_DELETE;
