  END IF;
  COMMIT;



  -- EXCEPTION HANDLING AREA
  EXCEPTION 
    WHEN OTHERS THEN
      V_err_code := SQLCODE;
      V_err_msg := SQLERRM;--err_msg := SUBSTR(SQLERRM, 1, 200);
      INSERT INTO TBL_LOG_ERROR(CODE, MESSAGE, RELEASE_POINT)
      VALUES(V_err_code, V_err_msg, $$plsql_unit);
      --RAISE I_AM_SORRY;

END PRC_REPORT_CREATE;
