  PROCEDURE PRC_REPORT_NOTIFICATION(
    I_JSON VARCHAR2, O_FAKE OUT VARCHAR2
  )AS

  INPAR_OPT_TYPE  VARCHAR2(20);
  INPAR_ID VARCHAR2(100);
 INPAR_TYPE VARCHAR2(100);
 INPAR_class VARCHAR2(100);
 INPAR_TITLE VARCHAR2(100);
 INPAR_STATUS VARCHAR2(100);
 INPAR_USER_ID VARCHAR2(100);
 INPAR_DESCRIPTION VARCHAR2(100);
 INPAR_REF_REPORT VARCHAR2(100);
 INPAR_REF_REPREQ VARCHAR2(100);
 INPAR_REF_REPPER_DATE VARCHAR2(100);
 inpar_flag VARCHAR2(100);
 OUTPAR_STATUS VARCHAR2(1000);
  v_json json;
BEGIN
 --------------------------------------------------------------------------------
  /*
  Programmer Name: Rasool.Jahani
  Editor Name:
  Release Date/Time:1396/05/21-10:00
  Edit Name:
  Version: 1
  Description:anjam amaliyat hay lazem baray darj - beroz resani - hazf va daryaft elanat
  */
  --------------------------------------------------------------------------------
  v_json := json(i_json);
  INPAR_OPT_TYPE := json_ext.get_string(v_json, 'optType');
  INPAR_ID := json_ext.get_string(v_json, 'id');
 INPAR_TYPE := json_ext.get_string(v_json, 'type');
 INPAR_class := json_ext.get_string(v_json, 'class');
 INPAR_TITLE := json_ext.get_string(v_json, 'title');
 INPAR_STATUS := json_ext.get_string(v_json, 'status');
 INPAR_USER_ID := json_ext.get_string(v_json, 'user');
 INPAR_DESCRIPTION := json_ext.get_string(v_json, 'des');
 INPAR_REF_REPORT := json_ext.get_string(v_json, 'report');
 INPAR_REF_REPREQ := json_ext.get_string(v_json, 'repReq');
 INPAR_REF_REPPER_DATE := json_ext.get_string(v_json, 'repPerDate');
 inpar_flag := json_ext.get_string(v_json, 'flag');



IF INPAR_OPT_TYPE = 'insert' THEN 
  --=======INSERT================ Darj yek elan jadid.
INSERT
INTO TBL_NOTIFICATIONS
  (

    TITLE,
    TYPE,
    class,
    REF_USER,
    START_TIME,
    STATUS,
    DESCRIPTION,
    REF_REPORT
  )
  VALUES
  (

    INPAR_TITLE,
    INPAR_TYPE,
    INPAR_class,
    INPAR_USER_ID,
    sysdate,
    'progress',
    INPAR_DESCRIPTION,
    INPAR_REF_REPORT
  );
    Commit;

  select max(id) into O_FAKE from TBL_NOTIFICATIONS;
  Commit;
 END IF; 

IF INPAR_OPT_TYPE = 'update' THEN 
  --=======UPDATE================Berozresani yek elan mojod.
   UPDATE TBL_NOTIFICATIONS
   SET STATUS           =INPAR_STATUS 
   , END_TIME = sysdate 
   , REF_REPREQ = INPAR_REF_REPREQ
   , REF_REPPER_DATE = INPAR_REF_REPPER_DATE
   , flag = inpar_flag
   WHERE ID        = INPAR_ID
   ;
   Commit;
 END IF; 

IF INPAR_OPT_TYPE = 'delete' THEN
  --=======DELETE================  Haz yek elan mojod.
  DELETE
  FROM TBL_NOTIFICATIONS
  WHERE ID        = INPAR_ID;
  Commit;
END IF;

IF INPAR_OPT_TYPE = 'check' THEN
  --=======CHECK STATUS================  Bargardandan tamimi vaziyat yek elan mojod.
  SELECT 
  STATUS
  into O_FAKE
  FROM TBL_NOTIFICATIONS

  where ID = INPAR_ID;
Commit;
END IF;


IF INPAR_OPT_TYPE = 'getall' THEN
  --=======GET ALL================ Bargardandan tamami elanat mojod. 
  SELECT 
  'SELECT ID as "id",
  TITLE  as "title",
  TYPE as "type",
  class as "class",
  REF_USER  as "user",
  to_char(START_TIME,''yyyy/MM/dd'') as "startTime",
  to_char(END_TIME,''yyyy/mm/dd'') as "endTime",
    STATUS as "status",
  DESCRIPTION as "des"
  ,ref_repreq as "ref"
  ,ref_repper_date as "perDate"
  ,REF_REPORT as "refReport"
  , flag as "changed"
FROM TBL_NOTIFICATIONS
order by START_TIME desc
'

  into O_FAKE
  FROM dual;


END IF;

   IF (INPAR_OPT_TYPE = 'visited') then

 update pragg.TBL_NOTIFICATIONS set STATUS='visited' ,flag = 0 where id=INPAR_ID;
 commit;

 end if;


  END PRC_REPORT_NOTIFICATION;

