  FUNCTION FNC_REPORT_SHOW(
    I_JSON IN VARCHAR2
  ) RETURN CLOB AS
  r_query CLOB;
  v_category varchar2(200);
  v_type varchar2(200);
  v_report varchar2(200);
  V_REPREQ VARCHAR2(200);
  V_SHOWTYPE VARCHAR2(500);
  V_JSON JSON;
BEGIN
  V_JSON := JSON(I_JSON);
  v_repreq := json_ext.get_string(v_json, 'repReq');
  V_SHOWTYPE := json_ext.get_string(v_json, 'showType');
  -- GET REPORT ID OF THIS REPORT REQUEST
  IF V_REPREQ IS NOT NULL THEN -- ~FOR REPREQLESS REPORTS
    SELECT REF_REPORT_ID INTO V_REPORT
    FROM TBL_REPREQ
    WHERE ID = v_REPREQ;
    -- GET TYPE & CAT OF THIS REPORT
    SELECT CATEGORY, TYPE
    INTO V_CATEGORY, V_TYPE
    FROM TBL_REPORT
    WHERE ID = V_REPORT;
  END IF;

  R_QUERY := 'NO MATCH!';
  -- RETURN CORESPONDING QUERY FOR SHOWING REPORT RESULT
  IF V_TYPE = '107' THEN 
    R_QUERY := DYNAMIC_LQ.FNC_GET_QUERY_REPORT(v_REPREQ);
  ELSIF V_CATEGORY = 'WES' AND V_TYPE = 'deposit' THEN  
    R_QUERY := DYNAMIC_LQ.PKG_WES.FNC_GET_DEP_VALUE(v_REPREQ);
  ELSIF V_CATEGORY = 'AES'  THEN 
    R_QUERY := DYNAMIC_LQ.PKG_AES.FNC_GET_ALL_STREAMS(v_REPREQ);
  ELSIF V_CATEGORY = 'idps' OR V_CATEGORY = 'IDPS' THEN
    R_QUERY := CASE WHEN V_SHOWTYPE = 'column' THEN PKG_IDPS.FNC_IDPS_GET_DETAIL_NAME(V_REPREQ)
                    ELSE PKG_IDPS.FNC_IDPS_FINAL_RESULT(V_REPREQ, json_ext.get_string(v_json, 'function')) 
               END;
    ELSIF V_TYPE = 'duedate_loan' THEN
    R_QUERY := CASE WHEN V_SHOWTYPE = 'tree'    THEN PRAGG.PKG_DUE_DATE_LOAN.FNC_DUE_DATE_GET_TREE()
                    WHEN V_SHOWTYPE = 'leaf'    THEN PRAGG.PKG_DUE_DATE_LOAN.FNC_DUE_DATE_GET_LEAF(json_ext.get_string(v_json, 'repReq'))
                    WHEN V_SHOWTYPE = 'detail'  THEN PRAGG.PKG_DUE_DATE_LOAN.FNC_DUE_DATE_GET_DETAIL(json_ext.get_string(v_json, 'repReq'), json_ext.get_string(v_json, 'type'))
                    WHEN V_SHOWTYPE = 'column'  THEN PRAGG.FNC_GET_DETAIL_TIMING (json_ext.get_string(v_json, 'repReq'))
               END;
  ELSIF V_TYPE = 'duedate' THEN
    R_QUERY := CASE WHEN V_SHOWTYPE = 'tree'    THEN PRAGG.PKG_DUE_DATE.FNC_DUE_DATE_GET_TREE()
                    WHEN V_SHOWTYPE = 'leaf'    THEN PRAGG.PKG_DUE_DATE.FNC_DUE_DATE_GET_LEAF(json_ext.get_string(v_json, 'repReq'))
                    WHEN V_SHOWTYPE = 'detail'  THEN PRAGG.PKG_DUE_DATE.FNC_DUE_DATE_GET_DETAIL(json_ext.get_string(v_json, 'repReq'), json_ext.get_string(v_json, 'type'))
                    WHEN V_SHOWTYPE = 'column'  THEN PRAGG.FNC_GET_DETAIL_TIMING (json_ext.get_string(v_json, 'repReq'))
               END;
   ELSIF((V_CATEGORY = 'gap' or V_CATEGORY = 'liquidity') AND (V_TYPE = 'rial' OR V_TYPE = 'currency' OR V_TYPE = 'composite' OR V_TYPE = 'compositeBranch' OR V_TYPE = 'rialBranch' OR V_TYPE = 'currencyBranch')) THEN 
    R_QUERY := CASE WHEN V_SHOWTYPE = 'gap'     THEN PRAGG.GET_QUERY_GAP(json_ext.get_string(v_json, 'repReq'))
                    WHEN V_SHOWTYPE = 'column'  THEN FNC_GET_DETAIL_TIMING(json_ext.get_string(v_json, 'repReq'))
                    ELSE fnc_GET_QUERY_REPORT_II(json_ext.get_string(v_json, 'repReq'), json_ext.get_string(v_json, 'curType'))
               END;
  ELSIF(upper(V_CATEGORY) = upper('dddr') AND upper(V_TYPE) = upper('dddr')) THEN 
    R_QUERY := CASE WHEN V_SHOWTYPE = 'detailChild' THEN PKG_DUE_DATE_DEPOSIT_RATE.FNC_DDDR_GET_detail_child(json_ext.get_string(v_json, 'repReq'))
                    WHEN V_SHOWTYPE = 'detailTree'  THEN PKG_DUE_DATE_DEPOSIT_RATE.fnc_dddr_get_tree_detail(V_REPORT)
                    WHEN V_SHOWTYPE = 'count'       THEN PKG_DUE_DATE_DEPOSIT_RATE.FNC_DDDR_GET_count(json_ext.get_string(v_json, 'repReq'))
               END;
  ElSIF(UPPER(V_CATEGORY) = UPPER('ddlt') AND UPPER(V_TYPE) = UPPER('ddlt')) THEN 
    R_QUERY := CASE WHEN V_SHOWTYPE = 'detailChild'            THEN PKG_DUE_DATE_LOAN_TYPE.FNC_DDLT_GET_detail_child(json_ext.get_string(v_json, 'repReq'))
                    WHEN V_SHOWTYPE = 'detailTree'             THEN PKG_DUE_DATE_LOAN_TYPE.fnc_DDLT_get_tree_detail(V_REPORT)
                    WHEN V_SHOWTYPE = 'count'                  THEN PKG_DUE_DATE_LOAN_TYPE.fnc_DDLT_GET_count(json_ext.get_string(v_json, 'repReq'))
               END;
  ELSIF(UPPER(V_CATEGORY) = UPPER('duration2') AND UPPER(V_TYPE) = UPPER('duration2')) THEN 
    R_QUERY := CASE WHEN V_SHOWTYPE = 'durationGapProfile'            THEN pkg_du_gap_prof.fnc_get_DU_GAP_PROF(json_ext.get_string(v_json, 'repReq'), json_ext.get_string(v_json, 'durationGapType'))
                    WHEN V_SHOWTYPE = 'durationGapProfileSens'        THEN pkg_du_gap_prof.fnc_get_DU_GAP_PROF_sens(json_ext.get_string(v_json, 'repReq'), json_ext.get_string(v_json, 'durationGapType'))
               END;
  ELSIF(UPPER(V_CATEGORY) = UPPER('GAP_NIIM') AND UPPER(V_TYPE) = UPPER('GAP_NIIM')) THEN 
    R_QUERY := CASE WHEN V_SHOWTYPE = 'reportQuery'           THEN  PKG_GAP_NIIM.FNC_GAP_NIIM_GET_REPORT_QUERY(json_ext.get_string(v_json, 'repReq'))
                    WHEN V_SHOWTYPE = 'timingDetail'          THEN PKG_GAP_NIIM.FNC_GAP_NIIM_GET_DETAIL_TIMING(json_ext.get_string(v_json, 'repReq'))
                    WHEN V_SHOWTYPE = 'reportSens'            THEN PKG_GAP_NIIM.FNC_GAP_NIIM_GET_REPORT_sens(json_ext.get_string(v_json, 'repReq'))
               END;
  ELSE -- without repreq
    R_QUERY := CASE WHEN V_SHOWTYPE = 'concRegion'                 THEN PKG_CONC.FNC_GET_REGION ()
                    WHEN V_SHOWTYPE = 'concTop100'                 THEN PKG_CONC.FNC_GET_TOP100_CUSTOMER(json_ext.get_string(v_json, 'reg'))
                    WHEN V_SHOWTYPE = 'concTop100Detail'           THEN PKG_CONC.FNC_GET_TOP100_CUSTOMER_DETAIL(json_ext.get_string(v_json, 'reg'))
                    WHEN V_SHOWTYPE = 'concPageNumber'             THEN pkg_conc.FNC_PAGE_NUMBER(json_ext.get_string(v_json, 'pageSize'))
                    WHEN V_SHOWTYPE = 'concStateReport'            THEN pkg_conc.FNC_CONC_STATE_REPORT()
                    WHEN V_SHOWTYPE = 'concMainReport'             THEN pkg_conc.FNC_CONC_MAIN_REPORT()
                    WHEN V_SHOWTYPE = 'concStateDetailReport'      THEN pkg_conc.FNC_CONC_STATE_DETAIL_REPORT(json_ext.get_string(v_json, 'state'))
                    WHEN V_SHOWTYPE = 'ledgerReport'               THEN FNC_GET_LEDGER_REPORT(json_ext.get_string(v_json, 'date'), json_ext.get_string(v_json, 'currency'))
                    WHEN V_SHOWTYPE = 'dueDateForm'                THEN akin.FNC_GET_DUE_DATE_FORM()
                    WHEN V_SHOWTYPE = 'NOPResult'                  THEN PKG_NOP_SIMPLE.FNC_NOP_RESULT(json_ext.get_string(v_json, 'date'))
                    WHEN V_SHOWTYPE = 'NOPSimpleTree'              THEN PKG_NOP_SIMPLE.FNC_NOP_simple_tree()
                    WHEN V_SHOWTYPE = 'NOPDetial'                  THEN pkg_nop_simple.FNC_NOP_GET_DETAIL_NAME(json_ext.get_string(v_json, 'date'))
                    WHEN V_SHOWTYPE = 'NOPResultGold'              THEN pkg_nop_simple.FNC_NOP_RESULT_gold(json_ext.get_string(v_json, 'date'))
                    WHEN V_SHOWTYPE = 'ledgerAvailableDates'       THEN FNC_GET_LEDGER_AVA_DATES()
                    WHEN V_SHOWTYPE = 'costumerConcAvailableDates' THEN PKG_CUSTOMER_CONC.FNC_GET_AVAILABLE_DATE()
                    WHEN V_SHOWTYPE = 'costumerConcFinalResult'    THEN PKG_CUSTOMER_CONC.FNC_CUSTOMER_CONC_FINAL_RESULT(json_ext.get_string(v_json, 'date'),json_ext.get_string(v_json, 'modalityType'))
                    WHEN V_SHOWTYPE = 'ledgerBranch'               THEN FNC_GET_REPORT_LEDGER_BRANCH(json_ext.get_string(v_json, 'branches'),json_ext.get_string(v_json, 'date'))
                    WHEN V_SHOWTYPE = 'currencyCompare'            THEN PKG_BALANCE_SHEET.FNC_COMPARE_BALANCE_SHEET(json_ext.get_string(v_json, 'date1'),json_ext.get_string(v_json, 'currency1'),json_ext.get_string(v_json, 'date2'),json_ext.get_string(v_json, 'currency2'))
                    WHEN V_SHOWTYPE = 'branchCompare'              THEN PKG_BRANCH_BALANCE_SHEET.FNC_COMPARE_BRANCH_BR_SHEET(json_ext.get_string(v_json, 'date1'),json_ext.get_string(v_json, 'branch1'),json_ext.get_string(v_json, 'date2'),json_ext.get_string(v_json, 'branch2'))
                   ELSE  'NO MATCH'
               END;
  END IF;


  RETURN R_QUERY;

END FNC_REPORT_SHOW;
