  FUNCTION FNC_REPORT_INFO(
    i_json varchar2
  ) RETURN VARCHAR2 AS
   R_QUERY VARCHAR2(32000);
   v_json json;
   V_TYPE VARCHAR2(100);
   V_CATEGORY VARCHAR2(100);
   V_REPORT VARCHAR2(500);
   V_INFOTYPE VARCHAR2(200);
  VAR    VARCHAR2(32000);
BEGIN
  V_JSON := json(i_json);
  V_REPORT := json_ext.get_string(v_json, 'id');--report
  V_INFOTYPE := json_ext.get_string(v_json, 'infoType');


  IF V_REPORT IS NOT NULL THEN
    SELECT TYPE, CATEGORY INTO V_TYPE, V_CATEGORY
    FROM TBL_REPORT
    WHERE ID = V_REPORT;

    IF UPPER(V_TYPE) = 'IDPS' THEN 
      R_QUERY := PKG_IDPS.FNC_IDPS_GET_REPORT_INFO(V_REPORT);
    ELSIF V_TYPE = 'duedate' OR V_TYPE = 'duedate_loan' THEN
      R_QUERY := FNC_GET_REPORT_PROFILE(V_REPORT);
    ELSIF((V_CATEGORY = 'gap' or V_CATEGORY = 'liquidity') AND (V_TYPE = 'rial' OR V_TYPE = 'currency' OR V_TYPE = 'composite' OR V_TYPE = 'compositeBranch' OR V_TYPE = 'rialBranch' OR V_TYPE = 'currencyBranch')) THEN 
      R_QUERY := FNC_GET_REPORT_PROFILE(V_REPORT);
    ELSIF(V_CATEGORY = 'GAP_NIIM' or V_TYPE = 'GAP_NIIM') THEN
      R_QUERY := 'SELECT ID as "id",
                  NAME as "name",
                  DES as "description",
                  CREATE_DATE as "createDate",
                  REF_USER as "refUser",
                  STATUS as "status",
                  CATEGORY as "category",
                  ref_timing_profile as "dateProfileId",
                  ref_dep_profile as "depositProfileId",
                  ref_lon_profile as "loanProfileId",
                  ref_cus_profile as "customerProfileId",
                  ref_cur_profile as "currencyProfileId",
                  ref_brn_profile as "branchProfileId",
                  timing_profile_type as "dateType",
                  REF_LEDGER_PROFIEL as "tahlilHassassiat",
                  first_date as "tasirHassassiat"
                 FROM TBL_REPORT 
                where id = '
                || v_report || ' and upper(category) = ''GAP_NIIM'' order by id';
    ELSIF(V_CATEGORY = 'duration2' or V_TYPE = 'duration2') THEN
    --dar sistem qabli farq dASt: refLoan=loanProfileId
    --barAye hamin bAznevisi Sod
      R_QUERY :=  'SELECT ID as "id",
                          NAME as "name",
                          DES as "description",
                          FIRST_DATE as "firstDate",
                          CREATE_DATE as "createDate",
                          REF_USER as "refUser",
                          STATUS as "status",
                          CATEGORY as "category",
                          ref_dep_profile as "depositProfileId"
                                        ,REF_LON_PROFILE as "loanProfileId"
                                        ,REF_CUS_PROFILE as "customerProfileId"--DAR SYSTEM GHABLI FARGH DASHT : refLoan
                                        ,bazeh as "changeRate",
                                        REF_BRN_PROFILE as "tahlilHasasiat"
                                        ,REF_TIMING_PROFILE as "tasir"
                          FROM TBL_REPORT 
                          where id = '|| V_REPORT || ' and upper(category) = ''DURATION2''';
    ELSE 
    R_QUERY := 'SELECT   
                ID as "id",  
                NAME as "name",  
                TYPE as "type", 
                CATEGORY as "category",
                DES as "description",
                REF_DEP_PROFILE as "depositProfileId",  
                REF_LON_PROFILE as "loanProfileId",
                REF_BRN_PROFILE AS "branchProfileId",
                REF_CUS_PROFILE AS "customerProfileId",
                REF_LEDGER_PROFIEL AS "ledgerProfileId",
                REF_TIMING_PROFILE AS "timingProfileId",
                REF_CUR_PROFILE AS "currencyProfileId",
                FIRST_DATE AS "firstDate",
                LAST_DATE AS "lastDate"
                FROM PRAGG.TBL_REPORT where ID = ' || V_REPORT;--report id
      END IF;
    ELSIF V_INFOTYPE = 'dddrDepositType' THEN 
      R_QUERY := PKG_DUE_DATE_DEPOSIT_RATE.FNC_DDDR_DEPOSIT_TYPE();
    ELSIF V_INFOTYPE = 'ddltLoanType' THEN 
      R_QUERY := PKG_DUE_DATE_LOAN_TYPE.FNC_DDLT_loan_type();
    END IF;

  RETURN R_QUERY;
  END FNC_REPORT_INFO;
