  CREATE OR REPLACE FUNCTION "PRAGG"."FNC_CLEAN_CALL" 
(
  I_DIRTY IN VARCHAR2 
) RETURN VARCHAR2 AS 
  v_prc_name varchar2(100);
  v_params varchar2(4000);
  r_clean varchar2(4000);
BEGIN
  r_clean := regexp_replace(i_dirty, '\s' ,' ');
  v_prc_name := regexp_substr(r_clean, 'PRC_.* \(');
  v_prc_name := trim(substr(v_prc_name, 1, length(v_prc_name)-1));
  v_params := regexp_substr(r_clean, '{.*{');
  v_params := trim(substr(v_params, 2, length(v_params)-2));
  v_params := regexp_replace(v_params, 'p(\d*):', '');
  v_params := regexp_replace(v_params, '\s*', '');
  v_params := '(' || replace(v_params, ',ret:', ',X') || ')';
  r_clean := v_prc_name || v_params;

  RETURN r_clean;

END fnc_CLEAN_CALL;

  CREATE OR REPLACE FUNCTION "PRAGG"."FNC_REPORT_SHOW" 
(
  I_REPREQ IN NUMBER,
  I_PARAM IN CLOB -- often is null but for special reports gets corespond values
) RETURN VARCHAR2 AS 
  r_query varchar2(4000);
  v_category varchar2(200);
  v_type varchar2(200);
  v_report varchar2(200);
BEGIN
  -- GET REPORT ID OF THIS REPORT REQUEST
  SELECT REF_REPORT_ID INTO V_REPORT
  FROM TBL_REPREQ
  WHERE ID = I_REPREQ;
  -- GET TYPE & CAT OF THIS REPORT
  SELECT CATEGORY, TYPE
  INTO V_CATEGORY, V_TYPE
  FROM TBL_REPORT
  WHERE ID = V_REPORT;


  -- RETURN CORESPONDING QUERY FOR SHOWING REPORT RESULT
  R_QUERY := CASE
              WHEN V_TYPE = '107' THEN DYNAMIC_LQ.FNC_GET_QUERY_REPORT(I_REPREQ) 
              WHEN V_CATEGORY = 'WES' AND V_TYPE = 'deposit' THEN DYNAMIC_LQ.PKG_WES.FNC_GET_DEP_VALUE(I_REPREQ)
              WHEN V_CATEGORY = 'WES' AND V_TYPE = 'loan'    THEN DYNAMIC_LQ.PKG_WES.FNC_GET_LON_VALUE(I_REPREQ)
              WHEN V_CATEGORY = 'AES'  THEN DYNAMIC_LQ.PKG_AES.FNC_GET_ALL_STREAMS(I_REPREQ)
              ELSE 'NO MATCH'
             END;

  RETURN R_QUERY;

END FNC_REPORT_SHOW;

  CREATE OR REPLACE FUNCTION "PRAGG"."TEST_ASHGHAL" 
(

 nparam in number 
) return varchar2 as 
first date;
var_fist_bazeh DATE;
var_last_bazeh date;
x number;
y date;
regdate date;

