  CREATE OR REPLACE FUNCTION "PRAGG"."FNC_CHECK_PERSIAN_LAST_MOTNTH" 
 (INPAR_CURRENT_DATE IN VARCHAR2 
 )
RETURN VARCHAR2 AS 
/*------------------------------------------------------------------------------*/
  /*
  Programmer Name: M.Javid.Ahmadi
  Editor Name: 
  Release Date/Time:1398/04/17-11:00
  Edit Name: 
  Version: 1
  Category:2
  Description:      
  */
/*------------------------------------------------------------------------------*/
VAR_DAY VARCHAR2(200);
VAR_NEXT_DAY VARCHAR2(2000);
VAR_PERSIAN_LAST_OF_MONTH VARCHAR(2000);

BEGIN
FOR I IN 1..30 
LOOP
  select REGEXP_SUBSTR((select to_char(TO_DATE(INPAR_CURRENT_DATE) +I,'yyyy/mm/dd','nls_calendar=persian') from dual),'[^/]+',1, LEVEL) AS "day" INTO VAR_NEXT_DAY
     FROM DUAL WHERE LEVEL = 3
  CONNECT BY REGEXP_SUBSTR((select to_char(TO_DATE(INPAR_CURRENT_DATE) +I,'yyyy/mm/dd','nls_calendar=persian') from dual), '[^/]+', 1, LEVEL) IS NOT NULL;
  IF VAR_NEXT_DAY = 1 THEN
    VAR_PERSIAN_LAST_OF_MONTH := TO_CHAR((TO_DATE(INPAR_CURRENT_DATE) +I )-1);
    EXIT;
  END IF;
END LOOP;
IF VAR_PERSIAN_LAST_OF_MONTH = INPAR_CURRENT_DATE THEN 
 RETURN 1;
 ELSE
  RETURN 0;
END IF;
END FNC_CHECK_PERSIAN_LAST_MOTNTH;
